    color([0.35, 0.15, 0.55])  // Purple hull color

    // Pea positions in pod
    pea([-5, 0, 0]);
    pea([5, 0, 0]);
    pea([15, 0, 0]);
}

// Individual pea
module pea(pos) {
    translate(pos)
    scale([1, 0.9, 0.8])
    sphere(d=8, $fn=20);

    // Pea eye (characteristic mark)
    color([0.15, 0.05, 0.25])
    translate([pos[0]+3, pos[1], pos[2]])
    rotate([0, 90, 0])
    cylinder(h=1, d=2, $fn=8);
}

// Pod stem
module stem() {
    color([0.10, 0.20, 0.08])
    translate([-25, 0, 0])
    rotate([0, -10, 0])
    cylinder(h=8, d1=3, d2=2, $fn=8);
}

// Assembly
union() {
    pea_pod();
    peas();
    stem();
}

