// Realistic Goan Bebinca - Traditional layered Goan dessert
// Approximately 70mm diameter x 25mm height

// Layer parameters
layers = 7;
layer_height = 2.5;
layer_spacing = 0.3;
diameter = 70;
wall_thickness = 2;

// Colors for realistic appearance
color_top = [0.96, 0.76, 0.42]; // Golden brown top
color_mid = [0.94, 0.82, 0.51]; // Medium brown layers
color_bottom = [0.92, 0.88, 0.60]; // Lighter base

// Texture details
caramel_spots = 12;
spot_radius = 2.5;
spot_depth = 0.5;

// Edge details
scallop_height = 1.5;
scallop_segments = 24;

// Main bebinca structure
module bebinca() {
    difference() {
        // Main layered cake body
        union() {
            for (i = [0:layers-1]) {
                color(interpolate_color(i)) {
                    translate([0, 0, i * (layer_height + layer_spacing)])
                        cylinder(
                            h = layer_height,
                            r1 = diameter/2 - (i * 1.5),  // Slight taper
                            r2 = diameter/2 - ((i+1) * 1.5),
                            $fn = 64
                        );
                }
            }
        }

        // Center hole (traditional bebinca has a hole)
        translate([0, 0, -1])
            cylinder(h = layers * (layer_height + layer_spacing) + 2, r = 8, $fn = 32);
    }
}

// Color interpolation function
function interpolate_color(layer) = [
    color_top[0] + (color_bottom[0] - color_top[0]) * layer / (layers - 1),
    color_top[1] + (color_bottom[1] - color_top[1]) * layer / (layers - 1),
    color_top[2] + (color_bottom[2] - color_top[2]) * layer / (layers - 1)
];

// Caramelized spots on top
module caramel_spots() {
    for (i = [0:caramel_spots-1]) {
        angle = i * 360 / caramel_spots;
        radius = diameter/2 * 0.7;
        translate([
            radius * cos(angle),
            radius * sin(angle),
            layers * (layer_height + layer_spacing) - spot_depth
        ]) {
            color([0.72, 0.52, 0.32])  // Darker caramel color
            sphere(r = spot_radius, $fn = 16);
        }
    }
}

// Scalloped edge detail
module scalloped_edge() {
    difference() {
        // Main edge
        cylinder(
            h = layers * (layer_height + layer_spacing),
            r = diameter/2 + wall_thickness,
            $fn = scallop_segments * 2
        );

        // Cut scallops
        for (i = [0:scallop_segments-1]) {
            angle = i * 360 / scallop_segments;
            translate([
                (diameter/2 + wall_thickness) * cos(angle),
                (diameter/2 + wall_thickness) * sin(angle),
                -1
            ]) {
                rotate([0, 0, angle])
                    translate([wall_thickness + 1, -wall_thickness - 1, 0])
                        cube([wall_thickness * 3, wall_thickness * 2, layers * (layer_height + layer_spacing) + 2]);
            }
        }
    }
}

// Bottom base with slight dome
module base() {
    color(color_bottom)
    translate([0, 0, -1])
        cylinder(h = 2, r1 = diameter/2 + wall_thickness, r2 = diameter/2 + wall_thickness - 1, $fn = 64);
}

// Assembly
union() {
    base();
    scalloped_edge();
    bebinca();
    caramel_spots();
}

