// Realistic Grass Food Item - Medium Complexity OpenSCAD Model
// Represents a bundle of fresh wheat grass with roots and soil

// Parameters
blade_height = 40;
blade_width = 2;
blade_thickness = 0.8;
num_blades = 15;
bundle_radius = 12;
soil_height = 8;
root_length = 15;

// Colors
grass_color = [0.2, 0.8, 0.1];
root_color = [0.7, 0.5, 0.3];
soil_color = [0.3, 0.2, 0.1];

// Grass blade module with slight curve and texture
module grass_blade(height, width, thickness) {
    color(grass_color) {
        // Main blade body
        for (i = [0:2:height]) {
            translate([0, 0, i])
            rotate([0, 0, sin(i * 0.5) * 3])
            scale([1 - (i / height) * 0.3, 1, 1])
            cube([width, thickness, 2], center = true);
        }

        // Blade tip (pointed)
        translate([0, 0, height + 1])
        rotate([0, 30, 0])
        scale([0.3, 1, 1])
        cylinder(h = 3, r1 = width/2, r2 = 0.1, center = true);
    }
}

// Root module
module root(length) {
    color(root_color) {
        translate([0, 0, -length/2])
        cylinder(h = length, r1 = 0.5, r2 = 0.1, $fn = 8);
    }
}

// Soil clump with texture
module soil_clump(radius, height) {
    color(soil_color) {
        difference() {
            // Main soil body
            cylinder(h = height, r = radius, $fn = 32);

            // Surface texture indentations
            for (i = [0:359:45]) {
                rotate([0, 0, i])
                translate([radius * 0.7, 0, height - 2])
                sphere(r = 2);
            }

            // Bottom hollow for printing
            translate([0, 0, -1])
            cylinder(h = 2, r = radius - 2, $fn = 32);
        }

        // Small soil particles on surface
        for (i = [0:8]) {
            rotate([0, 0, i * 137.5])
            translate([radius * 0.4 * sin(i), radius * 0.4 * cos(i), height - 0.5])
            sphere(r = 0.5 + sin(i) * 0.3);
        }
    }
}

// Main assembly
union() {
    // Soil base
    soil_clump(bundle_radius, soil_height);

    // Roots extending from bottom
    for (i = [0:num_blades - 1]) {
        angle = i * 360 / num_blades;
        distance = (i % 3) * 2 + 3;
        translate([distance * cos(angle), distance * sin(angle), 0])
        rotate([90 + sin(i) * 10, 0, angle])
        root(root_length);
    }

    // Grass blades growing from soil
    for (i = [0:num_blades - 1]) {
        angle = i * 360 / num_blades + (i % 2) * 15;
        distance = (i % 4) * 2 + 2;
        translate([distance * cos(angle), distance * sin(angle), soil_height])
        rotate([0, 0, angle + sin(i * 0.7) * 25])
        rotate([cos(i) * 5, sin(i) * 5, 0])
        grass_blade(blade_height + sin(i * 2) * 5, blade_width, blade_thickness);
    }

    // Central thicker blade for realism
    translate([0, 0, soil_height])
    rotate([0, 5, 0])
    grass_blade(blade_height * 1.1, blade_width * 1.5, blade_thickness * 1.2);
}

