        scale([1, 0.73, 1.37])
        sphere(d = egg_width - shell_thickness * 2 - 0.3);

        // Same crack opening
        translate([0, 0, egg_length/2 - 10])
        rotate([0, 30, 0])
        cylinder(h = 30, d1 = 19, d2 = 14, center = true);
