// Curd block with realistic texture and whey pockets
// Overall dimensions: 60x40x25mm, optimized for 3D printing

$fn = 32;

// Main curd parameters
curd_width = 60;
curd_depth = 40;
curd_height = 25;
wall_thickness = 1.2;

// Color definitions
curd_color = [1, 0.98, 0.85];  // Off-white creamy color
whey_color = [0.95, 0.95, 0.9];  // Slightly translucent whey

// Main module for the curd block
module curd_block() {
    color(curd_color) {
        difference() {
            // Base curd shape with rounded edges
            rounded_block(curd_width, curd_depth, curd_height, 5);

            // Internal structure - whey pockets and curd texture
            internal_structure();
        }
    }
}

// Create a rounded block primitive
module rounded_block(w, d, h, radius) {
    hull() {
        translate([radius, radius, 0]) cylinder(h=h, r=radius);
        translate([w-radius, radius, 0]) cylinder(h=h, r=radius);
        translate([w-radius, d-radius, 0]) cylinder(h=h, r=radius);
        translate([radius, d-radius, 0]) cylinder(h=h, r=radius);
    }
}

// Internal structure with whey pockets and curd texture
module internal_structure() {
    // Create whey pockets throughout the curd
    for (x = [10:15:curd_width-10]) {
        for (y = [10:12:curd_depth-10]) {
            for (z = [5:8:curd_height-5]) {
                translate([x, y, z]) {
                    // Random sized whey pockets
                    scale([0.8 + rand(x+y+z)*0.4, 
                           0.8 + rand(x+y+z+1)*0.4, 
                           0.8 + rand(x+y+z+2)*0.4]) {
                        sphere(2.5);
                    }
                }
            }
        }
    }

    // Surface texture - small dimples for curd grain appearance
    for (x = [5:8:curd_width-5]) {
        for (y = [5:8:curd_depth-5]) {
            // Only on top surface
            translate([x, y, curd_height-0.5]) {
                sphere(1.2);
            }
        }
    }

    // Side texture - irregularities
    for (z = [5:6:curd_height-5]) {
        for (i = [0:20]) {
            angle = i * 18;
            // Random placement along edges
            r = 5 + rand(i+z)*10;
            translate([curd_width/2 + cos(angle)*r, 
                      curd_depth/2 + sin(angle)*r, 
                      z]) {
                sphere(0.8);
            }
        }
    }
}

// Create whey drips on the surface
module whey_drips() {
    color(whey_color) {
        translate([curd_width/2, curd_depth/2, curd_height]) {
            sphere(1.5);
        }

        // Additional whey beads
        for (i = [0:4]) {
            angle = i * 72;
            r = 15 + rand(i)*10;
            translate([curd_width/2 + cos(angle)*r, 
                      curd_depth/2 + sin(angle)*r, 
                      curd_height]) {
                sphere(0.8 + rand(i)*0.4);
            }
        }
    }
}

// Simple pseudo-random function for texture variation
function rand(n) = (n*17 % 100)/100;

// Main assembly
curd_block();
whey_drips();

// Small curd granules scattered around for realism
for (i = [0:8]) {
    translate([rand(i*2)*curd_width, 
              rand(i*3)*curd_depth, 
              rand(i*4)*2]) {
        color(curd_color) sphere(1 + rand(i+5));
    }
}

// Whey Protein Powder Scoop - Realistic Style
// Medium complexity model with scoop and powder texture

$fn = 32;

// Main color - vanilla whey
