scoop_h = 25;
handle_l = 60;

module container() {
    // Outer container
    difference() {
        cylinder(d=container_d, h=container_h);
        translate([0, 0, base_thickness])
            cylinder(d=container_d-wall_thickness*2, h=container_h);
    }

    // Bottom base
    translate([0, 0, 0])
        cylinder(d=container_d, h=base_thickness);

    // Rim detail
    translate([0, 0, container_h-5])
        cylinder(d=container_d+2, h=5);
}

module powder_surface() {
    // Create realistic powder surface with slight irregularities
    for (i = [0:20]) {
        for (j = [0:20]) {
            x = (i-10) * 3.5;
            y = (j-10) * 3.5;
            h = 2 + sin(i*0.5) * cos(j*0.5) * 1.5;
            translate([x, y, container_h-10+h])
                sphere(d=4);
        }
    }
}

module scoop() {
    // Scoop bowl
    difference() {
        translate([0, 0, -scoop_h/2])
            scale([1, 1.2, 0.8])
                sphere(d=scoop_w);
        translate([0, 0, -scoop_h/2+5])
            scale([0.8, 1, 0.6])
                sphere(d=scoop_w-5);
    }

    // Handle
    translate([scoop_w/2, 0, 0])
        rotate([0, 90, 0])
            cylinder(d=8, h=handle_l);

    // Handle grip rings
    for (i = [1:5]) {
        translate([scoop_w/2 + i*10, 0, 0])
            rotate([0, 90, 0])
                cylinder(d=10, h=3);
    }
}

module label() {
    // Simple label band
    translate([0, container_d/2-1, container_h/2])
        rotate([90, 0, 0])
            cube([container_d-10, 30, 0.5], center=true);
}

