// Helper module for rounded cube
module rounded_cube(w, h, d, r) {
    hull() {
        for (x = [-1, 1])
        for (y = [-1, 1])
        for (z = [-1, 1])
        translate([x * (w/2 - r), y * (h/2 - r), z * (d/2 - r)])
        sphere(r = r);
