        hole_x = rands(-width/3, width/3, 1, random_seed + i * 2)[0];
        hole_y = rands(-height/4, height/4, 1, random_seed + i * 3)[0];
        hole_z = rands(-depth/3, depth/3, 1, random_seed + i * 4)[0];

        translate([hole_x, hole_y, hole_z])
        sphere(d = hole_size);
    }
