// Main cheese body with slight irregularities
module cheese_body() {
    color([1, 0.85, 0.3])  // Pale yellow cheese color
    difference() {
        // Main wheel shape
        union() {
            // Top slightly domed
            translate([0, 0, wheel_height/2])
                cylinder(d = wheel_diameter, h = wheel_height);

            // Gentle dome on top
            translate([0, 0, wheel_height])
                scale([1, 1, 0.3])
                    sphere(d = wheel_diameter * 0.9);
        }

        // Remove bottom to make flat base
        translate([0, 0, -1])
            cube([wheel_diameter + 2, wheel_diameter + 2, 2], center = true);
    }
}

// Cheese rind/crust
module cheese_crust() {
    color([0.8, 0.6, 0.2])  // Brownish crust color
    difference() {
        // Outer crust
        cylinder(d = wheel_diameter + 2, h = wheel_height + 2);

        // Inner hole for cheese
        cylinder(d = wheel_diameter, h = wheel_height + 4);
    }
}

// Create a single cheese hole
module cheese_hole(diameter, depth) {
    rotate([90, 0, 0])
        cylinder(d = diameter, h = depth * 1.2, $fn = 16);
}

// Generate random-looking holes with controlled distribution
module cheese_holes() {
    seed = 42;

    // Top surface holes
    for(i = [0:15]) {
        angle = (360 / 16) * i + (rands(-5, 5, 1, seed)[0]);
        radius = wheel_diameter/2 * 0.8 * (0.5 + rands(0, 0.5, 1, seed + i)[0]);
        x = cos(angle) * radius;
        y = sin(angle) * radius;

        // Random hole sizes
        size = rands(hole_min_diameter, hole_max_diameter, 1, seed + i * 2)[0];

        translate([x, y, wheel_height - 2])
            cheese_hole(size, wheel_height * 0.7);
    }

    // Side holes
    for(i = [0:11]) {
        angle = (360 / 12) * i + rands(-10, 10, 1, seed + 100)[0];
        height = rands(crust_thickness, wheel_height - crust_thickness, 1, seed + i * 3)[0];

        rotate([0, 0, angle])
            translate([wheel_diameter/2, 0, height])
                rotate([0, 90, 0])
                    cheese_hole(
                        rands(hole_min_diameter, hole_max_diameter * 0.8, 1, seed + i * 4)[0],
                        wheel_diameter * 0.3
                    );
    }

    // Central holes
    for(i = [0:7]) {
        angle = (360 / 7) * i;
        radius = wheel_diameter * 0.25;
        x = cos(angle) * radius;
        y = sin(angle) * radius;

        translate([x, y, wheel_height/2])
            sphere(d = rands(hole_min_diameter * 1.5, hole_max_diameter, 1, seed + i * 5)[0]);
    }
}

// Surface texture for realistic appearance
module surface_texture() {
    // Small dimples on surface
    for(i = [0:30]) {
        angle = rands(0, 360, 1, seed + 200)[0];
        radius = wheel_diameter/2 * rands(0.3, 0.95, 1, seed + i * 6)[0];
        x = cos(angle) * radius;
        y = sin(angle) * radius;
        z = rands(0, wheel_height, 1, seed + i * 7)[0];

        translate([x, y, z])
            sphere(d = 1.5);
    }
}

// Assembly
difference() {
    union() {
        cheese_crust();
        cheese_body();
    }

    cheese_holes();
}

