union() {
    surface_texture();
}

// GruyÃ¨re cheese wedge â€“ realistic style, medium complexity
// 70 mm long, 40 mm high, 40 mm wide at the rind
// Printable without supports â€“ holes slope inwards slightly

// Overall dimensions
length = 70;        // along X axis
height = 40;        // along Z axis
width  = 40;        // along Y axis at rind

// Wall / crust thickness
crust = 2.5;

// Hole parameters
hole_d   = 5;       // max diameter of eyes
hole_min = 2.5;     // min diameter of eyes
rows     = 4;       // rows along length
cols     = 3;       // columns across width
layers   = 3;       // vertical layers

// Create the cheese wedge
difference() {
    // Main body â€“ a wedge with crust
    union() {
        // Soft cheese core
        color("wheat") wedge_core();

        // Rind on two sides
        color("goldenrod") rind();
    }

    // Eyes (holes) â€“ randomised
    color("wheat") eyes();
}

// ------------------ Modules ------------------

module wedge_core() {
    // Tapered block from full width at rind (y=0) to tip (y=length)
    hull() {
        translate([0, 0, 0])
            cube([0.1, 0.1, height], center=true);
        translate([0, length, 0])
            cube([0.1, 0.1, 0.1], center=true);
        translate([width, 0, 0])
            cube([0.1, 0.1, height], center=true);
        translate([width, length, 0])
            cube([0.1, 0.1, 0.1], center=true);
    }
}

module rind() {
    // Add crust on top and one side
    difference() {
        // Top crust
        translate([width/2, length/2, height/2 + crust/2])
            cube([width + crust*2, length + crust*2, crust], center=true);

        // Side crust
        translate([-crust/2, length/2, height/2])
            cube([crust, length + crust*2, height + crust*2], center=true);

        // Remove overlap to keep only outer faces
        translate([width/2, length/2, height/2])
            cube([width, length, height], center=true);
    }
}

module eyes() {
    // Generate eyes in a randomised grid
    for (i = [0 : rows-1])
        for (j = [0 : cols-1])
            for (k = [0 : layers-1]) {
                // Position with jitter
                x = (j + 0.5 + rands(-0.3, 0.3, 1)[0]) * (width / (cols + 1));
                y = (i + 0.5 + rands(-0.3, 0.3, 1)[0]) * (length / (rows + 1));
                z = (k + 0.5) * (height / (layers + 1));

                // Slight taper to avoid overhangs
                taper = 0.9 - 0.2 * k / layers;

                translate([x, y, z])
                    scale([taper, taper, 1])
                        sphere(d = rands(hole_min, hole_d, 1)[0], $fn=16);
            }
}

