rind_thickness = 2;
body_color = [0.95, 0.85, 0.6];
rind_color = [0.6, 0.4, 0.2];
air_gap = 0.2;

// Cheese wheel with rind
module cheese_wheel() {
    difference() {
        // Main cheese body
        color(body_color) 
        cylinder(h = wheel_height, d = wheel_diameter - rind_thickness*2);

        // Hollow out center for printing
        translate([0, 0, -air_gap])
            cylinder(h = wheel_height + air_gap*2, d = wheel_diameter - rind_thickness*4);
    }
}

// Rind with cross-hatch pattern
module cheese_rind() {
    color(rind_color) {
        // Side rind
        difference() {
            cylinder(h = wheel_height, d = wheel_diameter);
            translate([0, 0, -air_gap])
                cylinder(h = wheel_height + air_gap*2, d = wheel_diameter - rind_thickness*2);
        }

        // Top rind
        difference() {
            translate([0, 0, wheel_height/2 - rind_thickness/2])
                cylinder(h = rind_thickness, d = wheel_diameter - rind_thickness*2);

            // Create cross-hatch grooves
            for(i = [-1, 1]) {
                for(j = [-1, 1]) {
                    rotate([0, 0, i * 45])
                    translate([0, j * 15, 0])
                    cube([30, 1.5, rind_thickness + 0.2], center = true);
                }
            }
        }

        // Bottom rind
        mirror([0, 0, 1])
            translate([0, 0, wheel_height/2 - rind_thickness/2])
            cylinder(h = rind_thickness, d = wheel_diameter - rind_thickness*2);
    }
}

// Cheese wedge cut from wheel
module cheese_wedge() {
    difference() {
        union() {
            cheese_wheel();
            cheese_rind();
        }

        // Cut wedge shape
        rotate([0, 0, -wedge_angle/2])
            translate([0, -wheel_diameter, -wheel_height])
            cube([wheel_diameter*2, wheel_diameter*2, wheel_height*3]);

        rotate([0, 0, wedge_angle/2])
            translate([0, -wheel_diameter, -wheel_height])
            cube([wheel_diameter*2, wheel_diameter*2, wheel_height*3]);
    }
}

// Small surface imperfections for realism
module surface_texture() {
    random_seed = 42;
    for(i = [0:15]) {
        angle = i * 24 + (i % 3) * 5;
        radius = wheel_diameter/2 - rind_thickness - (i % 5);

        translate([
            cos(angle) * radius, 
            sin(angle) * radius, 
            (i % 2) * wheel_height/2
        ]) {
            scale([1, 1, 0.3])
            sphere(d = 1 + (i % 3) * 0.5);
        }
    }
}

// Assemble the final model
translate([0, 0, wheel_height/2])
    cheese_wedge();

// Add surface detail
translate([0, 0, wheel_height/2])
    color(body_color)
    surface_texture();

