// Module for sugar dusting texture
module sugar_texture(base_size, density) {
    for(i = [0:density-1]) {
        for(j = [0:density-1]) {
            translate([
                -base_size/2 + i*(base_size/density) + (base_size/density)/2,
                -base_size/2 + j*(base_size/density) + (base_size/density)/2,
                height + 0.1
            ]) {
                scale([0.3, 0.3, 0.1]) {
                    sphere(1);
                }
            }
        }
    }
}

// Module for dulce de leche filling texture
module dulce_texture(base_size) {
    // Create ripple effect on top surface
    for(i = [0:20]) {
        rotate([0, 0, i*18]) {
            translate([0, 0, height - 2]) {
                color(color_dulce) {
                    scale([1, 0.2, 0.5]) {
                        cylinder(h=1, r1=base_size/2 - i*1.5, r2=base_size/2 - i*1.5 - 2);
                    }
                }
            }
        }
    }

    // Add some swirls
    for(i = [0:3]) {
        rotate([0, 0, i*90]) {
            translate([base_size/4, 0, height - 1]) {
                color(color_dulce) {
                    rotate_extrude(angle=90, $fn=16) {
                        translate([base_size/8, 0, 0]) {
