        // Rounded edges for realistic look
        translate([0, 0, thickness/2 - 0.5]) {
            color(color_cookie) {
                cube([size-2, size-2, thickness], center=true);
            }
        }

        // Add some cookie pores
        for(i = [0:15]) {
            translate([
                -size/2 + 5 + i*3,
                -size/2 + 5 + (i%4)*3,
