// Quiche Lorraine - 3D printable realistic style
// Features: flaky crust, egg custard, cheese cubes, bacon bits, chives

$fn = 36;  // Smooth curves

// Main dimensions (mm)
pie_diameter = 80;
pie_height = 25;
crust_thickness = 3;
custard_height = 15;

module quiche() {
    // Golden flaky pie crust
    color([0.9, 0.7, 0.45]) {
        // Bottom crust
        difference() {
            cylinder(h=crust_thickness, d=pie_diameter);
            translate([0, 0, -0.1])
                cylinder(h=crust_thickness+0.2, d=pie_diameter-crust_thickness*2);
        }

        // Fluted edge crust
        for(i=[0:30:359]) {
            rotate([0, 0, i])
                translate([pie_diameter/2-crust_thickness/2, 0, crust_thickness/2])
                    scale([1, 0.3, 1])
                        sphere(d=crust_thickness*2);
        }

        // Outer wall
        difference() {
            cylinder(h=pie_height, d=pie_diameter);
            translate([0, 0, -0.1])
                cylinder(h=pie_height+0.2, d=pie_diameter-crust_thickness*2);
        }
    }

    // Egg custard filling
    color([0.95, 0.9, 0.75]) {
        translate([0, 0, crust_thickness])
            cylinder(h=custard_height, d=pie_diameter-crust_thickness*2-2);
    }

    // Cheese cubes (GruyÃ¨re)
    color([0.95, 0.85, 0.5]) {
        for(i=[0:15]) {
            translate([
                (pie_diameter/2-15)*cos(i*45),
                (pie_diameter/2-15)*sin(i*45),
                crust_thickness+custard_height/2
            ])
                rotate([0, 0, i*30])
                    cube([3, 3, 2.5], center=true);
        }
    }

    // Bacon bits
    color([0.6, 0.2, 0.15]) {
        for(i=[0:20]) {
            translate([
                (pie_diameter/2-20)*cos(i*37),
                (pie_diameter/2-20)*sin(i*37),
                crust_thickness+custard_height/2+1
            ])
                rotate([0, 0, i*20])
                    scale([1, 0.4, 0.4])
                        cube([4, 2, 1.5], center=true);
        }
    }

    // Chive garnish
    color([0.2, 0.5, 0.2]) {
        for(i=[0:25]) {
            translate([
                (pie_diameter/2-10)*cos(i*29),
                (pie_diameter/2-10)*sin(i*29),
                crust_thickness+custard_height
            ])
                rotate([0, 0, i*45])
                    scale([0.1, 0.1, 1])
                        cylinder(h=3, d=1);
        }
    }

    // Browning on top surface
    color([0.9, 0.8, 0.6]) {
        translate([0, 0, crust_thickness+custard_height-0.5])
            scale([1, 1, 0.1])
                sphere(d=pie_diameter-crust_thickness*2-4);
    }
}

