// Meringue - Realistic Style
// Medium complexity model with textured surface and proper proportions

$fn = 32;

// Main meringue parameters
base_diameter = 45;
height = 35;
wall_thickness = 1.5;
top_radius = 25;

// Color definitions
meringue_color = [0.98, 0.95, 0.88];
meringue_brown = [0.92, 0.85, 0.70];

// Main meringue body with hollow interior
module meringue_body() {
    difference() {
        // Outer shell
        union() {
            // Base dome
            translate([0, 0, height/2]) 
                scale([1, 1, 0.8]) 
                    sphere(d = base_diameter);

            // Top swirl
            translate([0, 0, height - 5])
                sphere(d = top_radius);
        }

        // Hollow interior
        translate([0, 0, height/2])
            scale([1, 1, 0.8])
                sphere(d = base_diameter - 2*wall_thickness);

        translate([0, 0, height - 5])
            sphere(d = top_radius - 2*wall_thickness);
    }
}

// Create individual meringue peaks
module meringue_peak(size, height_var) {
    scale([1, 1, height_var]) 
        sphere(d = size);
}

// Generate textured surface with peaks
module textured_surface() {
    // Base peaks
    for(i = [0:5:360]) {
        for(j = [0:10:360]) {
            if(i % 15 == 0 && j % 20 == 0) {
                rotate([i, j, 0])
                    translate([base_diameter/2 * cos(i), 
                             base_diameter/2 * sin(i) * cos(j), 
                             height/2 + 5 * sin(j)])
                        meringue_peak(3 + rands(0, 2, 1)[0], 
                                    1.2 + rands(0, 0.8, 1)[0]);
            }
        }
    }

    // Top swirl peaks
    for(angle = [0:30:360]) {
        translate([top_radius/2 * cos(angle), 
                 top_radius/2 * sin(angle), 
                 height - 2])
            rotate([0, 0, angle])
                meringue_peak(2.5, 1.5);
    }
}

// Create bottom flat base
module flat_base() {
    difference() {
        cylinder(h = 2, d = base_diameter);
        translate([0, 0, -0.5])
            cylinder(h = 3, d = base_diameter - 2*wall_thickness);
    }
}

// Main assembly
color(meringue_color) {
    // Base structure
    union() {
        // Hollow body
        meringue_body();

        // Flat bottom
        translate([0, 0, -1])
            flat_base();
    }

    // Surface texture
    color(meringue_brown) {
        textured_surface();
    }

    // Additional small peaks for realism
    for(i = [1:15]) {
        rotate([rands(0, 360, 1)[0], rands(0, 360, 1)[0], rands(0, 360, 1)[0]])
            translate([rands(-20, 20, 1)[0], 
                     rands(-20, 20, 1)[0], 
                     rands(height/2, height, 1)[0]])
                meringue_peak(rands(1, 3, 1)[0], rands(1, 1.8, 1)[0]);
    }
}

// Pavlova - meringue base with whipped cream and fruit topping
// Overall dimensions ~70mm diameter, 35mm height
// Designed for 3D printing with no overhangs

$fa = 2;
$fs = 1;

// Main colors
color_meringue = [1, 0.95, 0.85];
color_cream = [1, 1, 1];
color_strawberry = [0.9, 0.2, 0.2];
color_kiwi = [0.4, 0.8, 0.2];
color_blueberry = [0.2, 0.2, 0.4];

// Meringue base with characteristic dome shape and rough texture
module meringue_base() {
    color(color_meringue) {
        // Main dome shape
        scale([1, 1, 0.7]) 
            sphere(d = 65, $fn = 60);

        // Create rough texture with random bumps
        for(i = [0:200]) {
            rotate([rand(0,360), rand(0,360), rand(0,360)])
                translate([rand(-30,30), rand(-30,30), rand(-10,20)])
                    sphere(d = rand(2,5), $fn = 8);
        }

        // Hollow out center for cream
        translate([0, 0, -10])
            cylinder(h = 25, d1 = 40, d2 = 50, $fn = 30);
    }
}

// Whipped cream dollops
module cream_topping() {
    color(color_cream) {
        // Multiple cream dollops
        for(i = [0:5]) {
            rotate([0, 0, i * 60])
                translate([rand(10,20), 0, 18])
                    scale([1, 1, 0.8])
                        sphere(d = rand(12,18), $fn = 20);
        }

        // Additional smaller dollops
        for(i = [0:8]) {
            rotate([0, 0, i * 45])
                translate([rand(15,25), 0, 20])
                    scale([0.8, 0.8, 0.6])
                        sphere(d = rand(6,10), $fn = 15);
        }
    }
}

// Strawberry slices
module strawberries() {
    color(color_strawberry) {
        for(i = [0:3]) {
            rotate([0, 0, i * 90 + 30])
                translate([rand(15,22), 0, 25])
                    scale([1, 1, 0.3])
                        sphere(d = 8, $fn = 6);
        }
    }
}

// Kiwi slices
module kiwi_slices() {
    color(color_kiwi) {
        for(i = [0:2]) {
            rotate([0, 0, i * 120 + 60])
                translate([rand(12,20), 0, 23])
                    scale([1.2, 1, 0.2])
                        sphere(d = 7, $fn = 6);
        }
    }
}

// Blueberries
module blueberries() {
    color(color_blueberry) {
        for(i = [0:5]) {
            rotate([0, 0, i * 72 + 15])
                translate([rand(8,18), rand(-5,5), 26])
                    sphere(d = 4, $fn = 10);
        }
    }
}

// Assemble the pavlova
union() {
    // Meringue base
    meringue_base();

    // Cream topping
    cream_topping();

    // Fruits
    strawberries();
    kiwi_slices();
    blueberries();
}

// Helper function for random values
function rand(min, max) = min + rands(0,1,1)[0] * (max - min);

// Realistic 3D Printable Pudding Model
// Medium complexity with caramel top, custard body, and serving bowl

$fn = 50;  // Smooth curves

module pudding() {
    // Pudding custard body
    color([1, 0.95, 0.8])  // Creamy yellow
    translate([0, 0, 15])
    difference() {
        // Main custard dome
        scale([1, 1, 0.7])
        sphere(d = 60);

        // Hollow out center for realistic thickness
        scale([0.9, 0.9, 0.65])
        sphere(d = 56);
    }

    // Caramel layer on top
    color([0.8, 0.5, 0.2, 0.9])  // Translucent amber
    translate([0, 0, 27])
    difference() {
        scale([1, 1, 0.15])
        sphere(d = 58);
        scale([0.95, 0.95, 0.1])
        sphere(d = 56);
    }

    // Caramel drips down sides
    for(i = [0:45:360]) {
        rotate([0, 0, i])
        translate([28, 0, 20])
        rotate([90, 0, 0])
        color([0.8, 0.5, 0.2, 0.8])
        scale([0.3, 0.3, 1.5 + (i % 90)/30])
        cylinder(h = 8, d1 = 4, d2 = 2);
    }

    // Small air bubbles in custard
    for(i = [0:20]) {
        color([1, 0.95, 0.8, 0.5])
        translate([
            20 * cos(i * 137.5),
            20 * sin(i * 137.5),
            15 + 10 * sin(i * 2.5)
        ])
        sphere(d = 1 + (i % 3));
    }
}

module serving_bowl() {
    // Glass serving bowl
    color([0.9, 0.95, 1, 0.4])  // Transparent glass
    difference() {
        // Outer bowl
        cylinder(h = 25, d1 = 80, d2 = 70);

        // Inner hollow
        translate([0, 0, 2])
        cylinder(h = 25, d1 = 76, d2 = 66);
    }

    // Bowl rim
    color([0.8, 0.85, 0.9, 0.5])
    translate([0, 0, 25])
    cylinder(h = 3, d1 = 82, d2 = 80);

    // Bowl base
    color([0.8, 0.85, 0.9, 0.5])
    cylinder(h = 8, d1 = 30, d2 = 40);
}

// Assembly
union() {
    serving_bowl();
    pudding();
}

$fs = 0.5;
$fa = 1;

module base_bowl() {
    difference() {
        cylinder(h = 20, r1 = 35, r2 = 30, center = false);
        translate([0, 0, 2])
            cylinder(h = 18, r1 = 33, r2 = 28, center = false);
    }
}

module sponge_layer() {
    color([0.82, 0.71, 0.55])
    difference() {
        cylinder(h = 6, r = 29.5, center = false);
        for (i = [0:7]) {
            rotate([0, 0, i * 45])
                translate([10, 0, -1])
                    cube([20, 2, 8], center = true);
        }
    }
}

module custard_layer() {
    color([1, 0.9, 0.6])
    cylinder(h = 8, r = 29, center = false);
}

module fruit_piece(x, y) {
    color([0.8, 0.2, 0.2])
    translate([x, y, 0])
        scale([1, 0.7, 0.6])
            sphere(r = 3, $fn = 12);
}

module fruit_layer() {
    for (i = [0:11]) {
        angle = i * 30;
        radius = 20 + (i % 2) * 5;
        fruit_piece(
            radius * cos(angle),
            radius * sin(angle)
        );
    }
    fruit_piece(0, 0);
}

module cream_swirl() {
    color([1, 1, 0.95])
    for (i = [0:5]) {
        rotate([0, 0, i * 60])
            translate([12, 0, 0])
                cylinder(h = 8, r1 = 6, r2 = 4, center = false);
