// Realistic Parfait - 3D Printable Model
// Features: Glass cup, layered yogurt, berries, granola, and whipped cream

// Overall scale for 3D printing
scale_factor = 1.5;

// Colors
glass_color = [220/255, 240/255, 255/255, 0.3];
yogurt_color = [255/255, 245/255, 230/255];
strawberry_color = [220/255, 50/255, 60/255];
blueberry_color = [40/255, 50/255, 120/255];
granola_color = [180/255, 140/255, 80/255];
cream_color = [255/255, 255/255, 255/255];

// Glass cup
module glass_cup() {
    difference() {
        // Outer cylinder
        cylinder(h=45, r1=20, r2=25, $fn=64);

        // Inner cavity
        translate([0, 0, 1])
            cylinder(h=44, r1=18, r2=23, $fn=64);

        // Bottom thickness
        translate([0, 0, 0])
            cylinder(h=2, r1=20, r2=25, $fn=64);
    }
}

// Yogurt layer with wavy top surface
module yogurt_layer(height, thickness) {
    difference() {
        // Base yogurt layer
        translate([0, 0, height])
            cylinder(h=thickness, r1=18, r2=23, $fn=64);

        // Create wavy texture
        for(i = [0:15:345]) {
            rotate([0, 0, i])
                translate([0, 10, height + thickness - 0.5])
                    rotate([0, 90, 0])
                        cylinder(h=25, r=0.5, $fn=16);
        }
    }
}

// Strawberry pieces
module strawberry_piece(x, y, z) {
    color(strawberry_color)
        translate([x, y, z])
            scale([1, 0.7, 0.5])
                sphere(r=2.5, $fn=16);
}

// Blueberry pieces
module blueberry_piece(x, y, z) {
    color(blueberry_color)
        translate([x, y, z])
            sphere(r=1.8, $fn=12);
}

// Granola clusters
module granola_cluster(x, y, z) {
    color(granola_color)
    for(i = [0:3]) {
        translate([x + sin(i*90)*1.5, y + cos(i*90)*1.5, z])
            scale([1, 0.7, 0.4])
                rotate([0, 0, i*45])
                    cube([2, 1.5, 0.8], center=true);
    }
}

// Whipped cream swirl
module whipped_cream() {
    color(cream_color)
    for(i = [0:30:360]) {
        rotate([0, 0, i])
            translate([sin(i*3)*2, cos(i*3)*2, 42 + i/15])
                sphere(r=3 - i/60, $fn=16);
    }
}

// Main assembly
scale([scale_factor, scale_factor, scale_factor]) {
    // Glass container
    color(glass_color)
        glass_cup();

    // First yogurt layer
    color(yogurt_color)
        yogurt_layer(2, 8);

    // First berry layer
    for(i = [0:5]) {
        angle = i * 60;
        strawberry_piece(sin(angle)*15, cos(angle)*15, 10.5);
        blueberry_piece(sin(angle+30)*12, cos(angle+30)*12, 10.5);
    }

    // Granola layer
    for(i = [0:12]) {
        angle = i * 30;
        granola_cluster(sin(angle)*15, cos(angle)*15, 11);
    }

    // Second yogurt layer
    color(yogurt_color)
        yogurt_layer(12, 8);

    // Second berry layer
    for(i = [0:4]) {
        angle = i * 90;
        blueberry_piece(sin(angle)*12, cos(angle)*12, 20.5);
        strawberry_piece(sin(angle+45)*10, cos(angle+45)*10, 20.5);
    }

    // Final granola layer
    for(i = [0:8]) {
        angle = i * 45;
        granola_cluster(sin(angle)*12, cos(angle)*12, 21);
    }

    // Third yogurt layer
    color(yogurt_color)
        yogurt_layer(22, 6);

    // Whipped cream top
    whipped_cream();
}

// Add base for stability
color([200/255, 200/255, 200/255])
    translate([0, 0, -2])
        cylinder(h=2, r1=25, r2=25, $fn=64);

// Gelato in a cup with 3 flavors and chocolate drizzle
$fa = 1;
$fs = 0.5;

module cup() {
    // Outer cup
    color("white")
    difference() {
        cylinder(h = 35, r1 = 22, r2 = 18);
        translate([0, 0, 1])
        cylinder(h = 34, r1 = 20, r2 = 16);
    }

    // Cup lip
    color("white")
    translate([0, 0, 35])
    cylinder(h = 3, r1 = 22.5, r2 = 22);
}

module gelato_ripples(height, radius, color_name) {
    // Create rippled texture for gelato
    color(color_name)
    for(i = [0:5]) {
        rotate([0, 0, i * 60])
        translate([0, 0, height - i * 1.5])
        scale([1 + sin(i * 30) * 0.1, 1 + cos(i * 30) * 0.1, 1])
        cylinder(h = 3, r = radius - i * 1.2, $fn = 32);
    }
}

module chocolate_drizzle() {
    // Random chocolate drizzle pattern
    color("brown")
    for(i = [0:8]) {
        angle = i * 40 + rand(i) * 20;
        radius = 8 + rand(i) * 8;
        translate([cos(angle) * radius, sin(angle) * radius, 33])
        rotate([90, 0, angle])
        cylinder(h = 10 + rand(i) * 5, r = 0.8, $fn = 8);
    }
}

function rand(n) = (n * 13.7) % 1;

module gelato_scoop(base_height, color_name, offset_x, offset_y) {
    // Main gelato scoop
    color(color_name) {
        translate([offset_x, offset_y, base_height])
        scale([1, 1, 1.2])
        sphere(r = 15, $fn = 32);

        // Add texture lines
        for(i = [0:7]) {
            rotate([0, 0, i * 45])
            translate([offset_x + cos(i * 45) * 8, offset_y + sin(i * 45) * 8, base_height + 8])
            scale([0.3, 0.3, 1])
            sphere(r = 3, $fn = 16);
        }
    }
}

module sprinkles() {
    // Colorful sprinkles
    colors = ["red", "green", "yellow", "pink", "blue"];
    for(i = [0:20]) {
        color(colors[i % len(colors)])
        translate([
            -15 + rand(i * 2) * 30,
            -15 + rand(i * 2 + 1) * 30,
            33 + rand(i * 2 + 2) * 5
        ])
        rotate([rand(i * 3) * 360, rand(i * 3 + 1) * 360, rand(i * 3 + 2) * 360])
        scale([1, 0.3, 0.3])
        cylinder(h = 4, r = 0.5, $fn = 6);
    }
}

// Assemble the gelato
cup();

// Three flavors stacked
gelato_ripples(5, 19, "#FFB6C1");  // Strawberry
gelato_ripples(10, 17, "#F5DEB3"); // Vanilla
gelato_ripples(15, 15, "#228B22"); // Pistachio

// Three main scoops
gelato_scoop(18, "#FFB6C1", -8, -8);  // Strawberry
gelato_scoop(18, "#F5DEB3", 8, -5);   // Vanilla
gelato_scoop(18, "#228B22", 0, 9);    // Pistachio

chocolate_drizzle();
sprinkles();

// Realistic Kulfi in OpenSCAD
// A traditional Indian frozen dessert on a stick

// Overall scale factor
kulfi_scale = 1.0;

// Main module to create the kulfi
module kulfi() {
    // Base kulfi body (tapered popsicle shape)
    color("#F5DEB3")  // Creamy beige color
    difference() {
        // Main kulfi body - tapered cylinder
        cylinder(h = 60 * kulfi_scale, r1 = 18 * kulfi_scale, r2 = 22 * kulfi_scale, $fn = 32);

        // Top dome shape
        translate([0, 0, 60 * kulfi_scale])
        sphere(r = 22 * kulfi_scale, $fn = 32);

        // Cut off the bottom half of sphere
        translate([0, 0, 60 * kulfi_scale - 0.1])
        cube([50 * kulfi_scale, 50 * kulfi_scale, 25 * kulfi_scale], center = true);
    }

    // Vertical ridges on the kulfi body
    for (i = [0:7]) {
        rotate([0, 0, i * 45])
        translate([19.5 * kulfi_scale, 0, 30 * kulfi_scale])
        color("#F4D4A3")
        cube([3 * kulfi_scale, 1 * kulfi_scale, 60 * kulfi_scale], center = true);
    }

    // Top pistachio garnish
    for (i = [0:11]) {
        rotate([0, 0, i * 30])
        translate([15 * kulfi_scale, 0, 75 * kulfi_scale])
        color("#90EE90")
        scale([1, 0.6, 0.2])
        sphere(r = 2 * kulfi_scale, $fn = 8);
    }

    // Saffron strands
    for (i = [0:5]) {
        rotate([0, 0, i * 60 + 15])
        translate([10 * kulfi_scale, 0, 78 * kulfi_scale])
        color("#FF6347")
        rotate([90, 0, 90])
        cylinder(h = 12 * kulfi_scale, r = 0.3 * kulfi_scale, $fn = 6);
    }

    // The stick
    translate([0, 0, -30 * kulfi_scale])
    color("#D2691E")
    cylinder(h = 35 * kulfi_scale, r = 3.5 * kulfi_scale, $fn = 16);

    // Stick grip texture
    translate([0, 0, -22 * kulfi_scale])
    color("#8B4513")
    for (j = [0:3]) {
        translate([0, 0, j * 4 * kulfi_scale])
        cylinder(h = 2 * kulfi_scale, r = 4 * kulfi_scale, $fn = 16);
    }

    // Cardamom seeds on surface
    for (i = [0:15]) {
        angle = i * 137.5;  // Golden angle for natural distribution
        radius = 8 + (i % 3) * 4;
        height = 10 + (i % 5) * 8;
        rotate([0, 0, angle])
        translate([radius * kulfi_scale, 0, height * kulfi_scale])
        color("#8B7355")
        scale([1, 0.7, 0.4])
        sphere(r = 0.8 * kulfi_scale, $fn = 6);
    }

    // Subtle melting drips
    for (i = [0:4]) {
        rotate([0, 0, i * 72 + 20])
        translate([20 * kulfi_scale, 0, 15 * kulfi_scale])
        color("#F5DEB3")
        scale([1, 1, 1.5])
        sphere(r = 2 * kulfi_scale, $fn = 12);
    }
}

// Render the kulfi
kulfi();

// Semifreddo - Italian frozen dessert with layers
// Dimensions approximately 80mm long, 50mm wide, 35mm high

// Main parameters
length = 80;
width = 50;
height = 35;
layer_height = height / 4;
wall_thickness = 2;

// Color definitions
custard_color = [1, 0.95, 0.8];
chocolate_color = [0.4, 0.2, 0.1];
cream_color = [1, 1, 0.95];
berry_color = [0.8, 0.2, 0.4];

// Create the main log shape with rounded edges
module semifreddo_body() {
    difference() {
        // Main rectangular body with rounded top
        union() {
            // Base rectangle
            translate([0, 0, height/2])
                cube([length, width, height], center=true);

            // Rounded top
            translate([0, 0, height])
                scale([length/width, 1, 1])
                sphere(d=width, $fn=30);
        }

        // Remove bottom half to create flat base
        translate([0, 0, -1])
            cube([length*2, width*2, 2], center=true);
    }
}

// Create sponge cake layer pattern
module sponge_layer() {
    color(custard_color) {
        difference() {
            // Base layer
            translate([0, 0, layer_height/2])
                cube([length-4, width-4, layer_height], center=true);

            // Create sponge holes pattern
            for(x = [-length/2+5:5:length/2-5]) {
                for(y = [-width/2+5:5:width/2-5]) {
                    translate([x, y, layer_height/2])
                        sphere(d=2, $fn=8);
                }
            }
        }
    }
}

// Create chocolate layer with swirl pattern
module chocolate_layer() {
    color(chocolate_color) {
        difference() {
            // Main chocolate layer
            translate([0, 0, layer_height*1.5])
                cube([length-4, width-4, layer_height], center=true);

            // Swirl pattern
            for(i = [0:30:360]) {
                rotate([0, 0, i])
                translate([length/4, 0, layer_height/2])
                    scale([1, 0.1, 0.3])
                    sphere(d=5, $fn=20);
            }
        }
    }
}

// Create cream layer with piping texture
module cream_layer() {
    color(cream_color) {
        union() {
            // Base cream layer
            translate([0, 0, layer_height*2.5])
                cube([length-4, width-4, layer_height], center=true);

            // Piping details on top
            for(i = [-length/2+8:8:length/2-8]) {
                for(j = [-width/2+8:8:width/2-8]) {
                    translate([i, j, layer_height*3])
                        sphere(d=4, $fn=16);
                }
            }
        }
    }
}

// Create berry decoration
module berry_deco() {
    color(berry_color) {
        // Central berries
        translate([0, 0, height+2])
            sphere(d=6, $fn=16);
        translate([-10, 0, height+2])
            sphere(d=5, $fn=16);
        translate([10, 0, height+2])
            sphere(d=5, $fn=16);

        // Raspberry texture
        for(i = [0:15:360]) {
            rotate([0, 0, i])
            translate([0, 0, height+2])
                scale([0.3, 1, 0.3])
                sphere(d=6, $fn=8);
        }
    }
}

// Create chocolate shavings
module chocolate_shavings() {
    color(chocolate_color) {
        for(i = [1:8]) {
            rotate([0, 0, i*45])
            translate([length/3, 0, height+3])
                scale([0.2, 2, 0.1])
                rotate([90, 0, 0])
                cylinder(h=8, d1=2, d2=0.5, $fn=3);
        }
    }
}

// Assembly
union() {
    // Main body with rounded top
    difference() {
        color(cream_color) semifreddo_body();

        // Hollow out interior for printing
        scale([0.9, 0.9, 0.9])
        color(cream_color) semifreddo_body();
    }

    // Visible layers on cut face
    translate([length/2-2, 0, 0]) {
        sponge_layer();
        chocolate_layer();
        cream_layer();
    }

    // Decorations on top
    berry_deco();
    chocolate_shavings();
}

// Spoom (Italian semi-frozen dessert) - realistic style, medium complexity
// A cross between sorbet and mousse - light, airy, and frozen

// Main parameters
spoom_diameter = 60;      // Overall diameter in mm
spoom_height = 45;        // Overall height in mm
wall_thickness = 2;       // Wall thickness for 3D printing
texture_depth = 0.5;      // Surface texture depth

// Colors
$fn = 48;                 // Smooth curves

// Main spoom body - slightly tapered dome
module spoom_body() {
    color("honeydew") {
        difference() {
            // Main dome shape
            translate([0, 0, -spoom_height/2])
                scale([1, 1, 0.7])
                sphere(d = spoom_diameter);

            // Hollow out center
            translate([0, 0, -spoom_height/2 + wall_thickness])
                scale([1, 1, 0.7])
                sphere(d = spoom_diameter - 2*wall_thickness);

            // Flat bottom
            translate([0, 0, -spoom_height/2])
                cube([spoom_diameter*2, spoom_diameter*2, wall_thickness], center=true);
        }
    }
}

// Air bubble texture - characteristic of spoom's airy structure
module bubble_texture() {
    color("honeydew") {
        // Random small spheres for bubble effect
        for(i = [0:20]) {
            translate([
                (rand(i, 0) - 0.5) * spoom_diameter * 0.4,
                (rand(i, 1) - 0.5) * spoom_diameter * 0.4,
                (rand(i, 2) - 0.5) * spoom_height * 0.5 + spoom_height/4
            ])
                sphere(d = 1 + rand(i, 3) * 2);
        }
    }
}

// Fruit puree swirl on top
module fruit_swirl() {
    color("lightcoral") {
        // Create spiral pattern
        for(i = [0:30]) {
            angle = i * 30;
