// Waffle cone with texture
module waffle_cone(height, base_r, top_r) {
    // Main cone shape
    difference() {
        cylinder(h=height, r1=base_r, r2=top_r, $fn=64);

        // Hollow out the cone
        translate([0, 0, 2])
            cylinder(h=height-2, r1=base_r-2, r2=top_r-2, $fn=64);
    }

    // Waffle pattern - criss-cross lines
    for (i = [0:5:height]) {
        rotate([0, 0, 0])
            translate([0, 0, i])
                difference() {
                    cylinder(h=0.8, r1=base_r-(base_r-top_r)*(i/height), r2=base_r-(base_r-top_r)*((i+0.8)/height), $fn=64);
                    translate([0, 0, -0.1])
                        cylinder(h=1, r1=base_r-(base_r-top_r)*(i/height)-1.5, r2=base_r-(base_r-top_r)*((i+0.8)/height)-1.5, $fn=64);
                }
    }

    // Vertical ridges
    for (angle = [0:15:360]) {
        rotate([0, 0, angle])
            translate([base_r-1, 0, 0])
                cube([2, 0.5, height], center=true);
    }
}

// Ice cream scoop with realistic texture
module ice_cream_scoop(radius) {
    // Main scoop
    color("floralwhite")
    difference() {
        sphere(r=radius, $fn=64);

        // Flat bottom to sit on cone
        translate([0, 0, -radius+2])
            cube([radius*2, radius*2, radius], center=true);
    }

    // Surface texture - small spheres for ice cream texture
    for (i = [0:30]) {
        theta = rands(0, 360, 1)[0];
        phi = rands(30, 150, 1)[0];
        r = radius - 0.5;

        translate([
            r * sin(phi) * cos(theta),
            r * sin(phi) * sin(theta),
            r * cos(phi)
        ])
            sphere(r=0.5 + rands(0, 0.3, 1)[0], $fn=8);
    }
}

// Chocolate sauce drips
module chocolate_sauce(radius, drips) {
    color("saddlebrown")
    union() {
        // Thin layer on top
        translate([0, 0, radius-0.5])
            sphere(r=radius+1, $fn=64);

        // Drip details
        for (i = [0:drips-1]) {
            angle = i * (360/drips);
            drip_length = rands(5, 12, 1)[0];

            rotate([0, 0, angle])
                translate([radius-2, 0, radius-2])
                    rotate([90, 0, 0])
                        cylinder(h=drip_length, r1=2, r2=0.5, $fn=16);
        }
    }
}

// Assembly
