    // Waffle cone
    color("tan")
    waffle_cone(cone_height, cone_base, cone_top);

    // Ice cream on top
    translate([0, 0, cone_height-2])
        ice_cream_scoop(scoop_radius);

    // Chocolate sauce
    translate([0, 0, cone_height-2])
        chocolate_sauce(scoop_radius, chocolate_drips);
}

