    color(shadow_color) {
        translate([2, 1, -egg_height/4])
            scale([1, 0.9, 0.3])
                sphere(d = egg_width * 0.8);

        translate([-1, -1, -egg_height/3])
            scale([0.8, 0.7, 0.2])
                sphere(d = egg_width * 0.6);
    }
}

// Combine all elements
