// Helper function for random values
function rand(min, max) = min + (max - min) * rands(0, 1, 1)[0];

// Ovalbumin (Egg White) in Realistic Style
// Medium complexity 3D printable model

$fn = 50;  // Smooth curves for realistic appearance

// Main parameters
egg_white_length = 75;
egg_white_width = 55;
egg_white_height = 25;
wall_thickness = 2;

// Color definitions
egg_white_color = [0.98, 0.98, 0.95];
highlight_color = [1, 1, 1];

// Main egg white body with realistic organic shape
module egg_white_body() {
    // Create base ellipsoid shape
    difference() {
        scale([1, 0.9, 0.6]) 
            sphere(d = egg_white_length);

        // Trim bottom flat
        translate([0, 0, -egg_white_height/2 - 1])
            cube([egg_white_length*2, egg_white_width*2, 2], center = true);
    }
}

// Create texture bubbles for realistic appearance
module texture_bubbles() {
    random_seed = 42;
    for(i = [0:35]) {
        bubble_size = rands(1.5, 3.5, 1)[0];
        x_pos = rands(-egg_white_length/3, egg_white_length/3, 1)[0];
        y_pos = rands(-egg_white_width/3, egg_white_width/3, 1)[0];
        z_pos = rands(0, egg_white_height/2 - 2, 1)[0];

        translate([x_pos, y_pos, z_pos])
            color(highlight_color)
                sphere(d = bubble_size);
    }
}

// Create slight indentations for realistic surface
module surface_indentations() {
    for(i = [0:20]) {
        indent_size = rands(2, 4, 1)[0];
        x_pos = rands(-egg_white_length/2.5, egg_white_length/2.5, 1)[0];
        y_pos = rands(-egg_white_width/2.5, egg_white_width/2.5, 1)[0];
        z_pos = rands(-egg_white_height/4, egg_white_height/4, 1)[0];

        translate([x_pos, y_pos, z_pos])
            rotate([rands(0, 360, 1)[0], rands(0, 360, 1)[0], rands(0, 360, 1)[0]])
                scale([1, 1, 0.3])
                    sphere(d = indent_size);
    }
}

// Create thick edge around the perimeter
module thick_edge() {
    difference() {
        // Outer shell
        scale([1, 0.9, 0.6]) 
            sphere(d = egg_white_length);

        // Inner hollow
        scale([0.85, 0.76, 0.45]) 
            sphere(d = egg_white_length);

        // Trim bottom
        translate([0, 0, -egg_white_height/2 - 1])
            cube([egg_white_length*2, egg_white_width*2, 2], center = true);
    }
}

// Create pooling effect at bottom
module bottom_pool() {
    translate([0, 0, -egg_white_height/2 + 1])
        scale([1.1, 1, 0.2])
            cylinder(h = 3, d = egg_white_width * 0.8, center = true);
}

// Main assembly
union() {
    // Base egg white structure
    color(egg_white_color) {
        difference() {
            // Main body
            egg_white_body();

            // Hollow center for printing efficiency
            scale([0.8, 0.7, 0.4])
                sphere(d = egg_white_length * 0.9);
        }
    }

    // Add thick edge for realism
    color(egg_white_color) {
        thick_edge();
    }

    // Add bottom pooling
    color(egg_white_color) {
        bottom_pool();
    }

    // Add surface texture
    difference() {
        // Main body for texture
        scale([0.95, 0.85, 0.55]) 
            sphere(d = egg_white_length);

        // Surface indentations
        surface_indentations();
    }

    // Add highlight bubbles
    texture_bubbles();
}

// Add slight gloss effect
translate([0, 0, egg_white_height/2 - 1])
    scale([0.3, 0.3, 0.1])
        color(highlight_color)
            sphere(d = egg_white_length * 0.3);

// Ovomucin food item - realistic medium complexity
// Ovomucin appears as a fibrous, gel-like substance with a translucent appearance
