$fn = 32;

// Main ovomucin gel structure
module ovomucin_gel() {
    color([0.9, 0.95, 0.85, 0.7])  // Translucent off-white
    union() {
        // Central gel mass
        sphere(15);

        // Fibrous extensions
        for(i = [0:45:360]) {
            rotate([0, 0, i])
            translate([10, 0, 0])
            rotate([90, 0, 0])
            cylinder(h1 = 20, h2 = 0, r1 = 5, r2 = 2);
        }
    }
}

// Internal fibrous network
module fibrous_network() {
    color([0.85, 0.9, 0.75, 0.5])  // More translucent
    union() {
        // Horizontal fibers
        for(z = [-10:5:10]) {
            translate([0, 0, z])
            rotate([90, 0, 0])
            cylinder(h = 30, r = 0.5);
        }

        // Vertical fibers
        for(x = [-10:5:10]) {
            for(y = [-10:5:10]) {
                translate([x, y, 0])
                cylinder(h = 20, r = 0.5);
            }
        }

        // Diagonal fibers
        for(i = [0:30:330]) {
            rotate([0, 0, i])
            translate([0, 8, 0])
            rotate([45, 0, 0])
            cylinder(h = 25, r = 0.3);
        }
    }
}

// Surface texture details
