    color([0.95, 1, 0.9, 0.8])
    difference() {
        sphere(16);  // Slightly larger than gel

        // Small surface irregularities
        for(i = [0:20:359]) {
            for(j = [0:20:179]) {
                rotate([j, i, 0])
                translate([15, 0, 0])
                sphere(0.8);
            }
        }
    }
}

// Add some water droplets on surface
