    union() {
        for(i = [1:10]) {
            rotate([rand(i)*360, rand(i+10)*180, rand(i+20)*360])
            translate([15 + rand(i+30)*2, 0, 0])
            sphere(0.5 + rand(i+40)*0.5);
        }
    }
}

// Base support for 3D printing
