// Ovoglobulin Food Item - Realistic Style (Medium Complexity)
// A stylized egg white and yolk combination with texture

egg_diameter = 45;
egg_height = 55;
wall_thickness = 2;

// Main egg white body
module egg_white() {
    color("white") {
        difference() {
            // Main egg white shape - slightly flattened sphere
            scale([1, 1, 1.2]) 
                sphere(d=egg_diameter);

            // Hollow out the center for yolk placement
            translate([0, 0, -5])
                scale([1, 1, 1.3])
                    sphere(d=egg_diameter - wall_thickness*2);
        }
    }
}

// Realistic egg yolk with membrane
module egg_yolk() {
    color([1, 0.8, 0.1]) {
        union() {
            // Main yolk body
            translate([0, 0, 5])
                sphere(d=egg_diameter * 0.6);

            // Yolk membrane ridge
            translate([0, 0, 5])
                sphere(d=egg_diameter * 0.62);
        }
    }
}

// Egg white texture - small bumps for albumen structure
module egg_texture() {
    color("white") {
        for(i = [0:15]) {
            for(j = [0:7]) {
                angle = i * 24;
                height = j * 6 + 3;
                radius = egg_diameter/2 * sin(height/egg_height * 180) - wall_thickness;

                translate([cos(angle) * radius, sin(angle) * radius, height])
                    sphere(d=1.5);
            }
        }
    }
}

// Yolk surface texture - small oil droplets
module yolk_texture() {
    color([0.9, 0.7, 0.05]) {
        for(i = [0:20]) {
            angle = i * 18;
            for(j = [0:3]) {
                y_angle = j * 60;
                translate([
                    cos(angle) * sin(y_angle) * (egg_diameter * 0.28),
                    sin(angle) * sin(y_angle) * (egg_diameter * 0.28),
                    5 + cos(y_angle) * (egg_diameter * 0.28)
                ]) {
                    sphere(d=0.8);
                }
            }
        }
    }
}

// Base ring for stability
module base_ring() {
    color("white") {
        difference() {
            cylinder(h=3, d1=egg_diameter + 4, d2=egg_diameter + 2);
            cylinder(h=4, d=egg_diameter);
        }
    }
}

// Assembly
union() {
    base_ring();

    translate([0, 0, 3])
        egg_white();

    translate([0, 0, 3])
        egg_yolk();

    translate([0, 0, 3])
        egg_texture();

    translate([0, 0, 3])
        yolk_texture();
}

// Ovotransferrin (egg white protein) in realistic style
// Medium complexity OpenSCAD model

// Overall dimensions
protein_size = 80;

// Colors
color_protein = [0.95, 0.95, 0.85];
color_highlight = [1, 1, 0.9];
color_shadow = [0.9, 0.9, 0.8];

// Main protein body module
module protein_body() {
    color(color_protein) {
        // Central globular core
        sphere(d = protein_size * 0.8);

        // Add lobes
        translate([protein_size * 0.3, 0, 0])
            sphere(d = protein_size * 0.5);
        translate([-protein_size * 0.25, protein_size * 0.2, 0])
            sphere(d = protein_size * 0.4);
        translate([-protein_size * 0.25, -protein_size * 0.2, 0])
            sphere(d = protein_size * 0.4);
    }
}

// Surface texture for crystalline appearance
module surface_texture() {
    color(color_highlight) {
        for (i = [0:15]) {
            theta = i * 24;
            for (j = [0:7]) {
                phi = j * 22.5;
                x = cos(theta) * sin(phi) * protein_size * 0.4;
                y = sin(theta) * sin(phi) * protein_size * 0.4;
                z = cos(phi) * protein_size * 0.4;

                translate([x, y, z])
                    sphere(d = protein_size * 0.05);
            }
        }
    }
}

// Add grooves/folds characteristic of ovotransferrin
module protein_folds() {
    color(color_shadow) {
        // Main cleft
        translate([protein_size * 0.1, 0, 0])
            rotate([0, 90, 0])
                cylinder(h = protein_size * 0.8, d = protein_size * 0.15, $fn = 16);

        // Secondary grooves
        for (i = [1:3]) {
            angle = i * 120;
            rotate([0, 0, angle])
                translate([protein_size * 0.3, 0, 0])
                    rotate([0, 90, 0])
                        cylinder(h = protein_size * 0.4, d = protein_size * 0.08, $fn = 12);
        }
    }
}

// Iron binding sites (reddish spots)
module iron_sites() {
    color([0.8, 0.4, 0.3]) {
        translate([protein_size * 0.2, protein_size * 0.2, protein_size * 0.2])
            sphere(d = protein_size * 0.08);
        translate([-protein_size * 0.2, -protein_size * 0.2, protein_size * 0.2])
            sphere(d = protein_size * 0.08);
    }
}

// Base platform for printing
module base() {
    color([0.9, 0.9, 0.9]) 
        cylinder(h = 2, d = protein_size * 1.2, $fn = 32);
}

// Assembly
union() {
    base();

    translate([0, 0, 3])
        union() {
            protein_body();
            surface_texture();
            protein_folds();
            iron_sites();
        }
}

