// Realistic Egg Yolk with Medium Complexity
// 3D printable, ~70mm diameter, includes texture and color variations

// Main yolk parameters
yolk_radius = 35;
yolk_height = 30;
wall_thickness = 2;

// Color definitions
yolk_color = [0.95, 0.65, 0.15, 1.0];  // Bright yellow-orange
darker_yolk = [0.85, 0.55, 0.10, 1.0]; // Slightly darker for texture
highlight_color = [1.0, 0.8, 0.2, 0.6];  // Glossy highlight

// Create the main yolk body
module yolk_body() {
    color(yolk_color) {
        // Main sphere slightly flattened
        scale([1, 1, 0.85])
        sphere(r=yolk_radius, $fn=100);

        // Add slight bulge at top for realistic shape
        translate([0, 0, yolk_height * 0.7])
        scale([0.95, 0.95, 0.4])
        sphere(r=yolk_radius * 0.8, $fn=50);
    }
}

// Create membrane texture around yolk
module membrane_texture() {
    difference() {
        // Outer membrane shell
        color([1, 0.9, 0.6, 0.3])
        scale([1, 1, 0.85])
        sphere(r=yolk_radius + wall_thickness, $fn=100);

        // Remove inner part to create hollow shell
        scale([1, 1, 0.85])
        sphere(r=yolk_radius, $fn=100);
    }
}

// Add realistic surface texture with small bumps
module surface_texture() {
    // Create random bumps across surface using golden ratio
    phi = (1 + sqrt(5)) / 2;

    for(i = [0:5:300]) {
        theta = i * phi * 137.5;  // Fibonacci spiral
        phi_rad = acos(1 - 2 * (i / 300));

        x = yolk_radius * 0.95 * sin(phi_rad) * cos(theta);
        y = yolk_radius * 0.95 * sin(phi_rad) * sin(theta);
        z = yolk_radius * 0.85 * 0.85 * cos(phi_rad);

        // Skip bumps near bottom to prevent printing issues
        if(z > -yolk_radius * 0.5) {
            translate([x, y, z])
            color(darker_yolk)
            scale([1, 1, 0.5])
            sphere(r=1 + sin(i) * 0.5, $fn=8);
        }
    }
}

// Add glossy highlight on top
module highlight() {
    color(highlight_color) {
        translate([yolk_radius * 0.3, -yolk_radius * 0.2, yolk_height * 0.8])
        scale([1, 0.7, 0.3])
        sphere(r=8, $fn=20);

        translate([-yolk_radius * 0.1, yolk_radius * 0.3, yolk_height * 0.75])
        scale([0.8, 1.2, 0.2])
        sphere(r=5, $fn=15);
    }
}

// Create slight indent for realistic yolk appearance
module yolk_indent() {
    difference() {
        yolk_body();

        // Subtle indent at top
        translate([0, 0, yolk_height * 0.9])
        scale([0.8, 0.8, 0.5])
        sphere(r=yolk_radius * 0.3, $fn=30);
    }
}

// Main assembly
union() {
    // Solid yolk (for printing)
    yolk_indent();

    // Optional membrane (uncomment for hollow yolk)
    // membrane_texture();

    // Surface details
    surface_texture();

    // Highlights for glossy appearance
    highlight();
}

// Add small support base for printing
translate([0, 0, -yolk_height * 0.85 - 1])
color(yolk_color)
cylinder(h=2, r1=yolk_radius * 0.3, r2=yolk_radius * 0.4, $fn=32);

// Realistic 3D printable scallop shell (Pecten sp.) ~70 mm wide
// Medium complexity â€“ ribs, fan shape, hinge, curved bowl

module shell() {
    color("bisque")
    difference() {
        // Outer convex shell
        scale([1, 0.85, 0.4])
        sphere(d = 70, $fn = 120);

        // Carve inner cavity â€“ leave 2 mm walls
        translate([0, 0, -4])
        scale([0.88, 0.75, 0.45])
        sphere(d = 70, $fn = 120);

        // Flatten top for 3D-printability
        translate([0, 0, 10])
        cube([80, 80, 20], center = true);
    }

    // Radial ribs
    for (a = [0:12:359]) {
        rotate([0, 0, a])
        translate([0, 0, -2])
        linear_extrude(height = 7, twist = 5, scale = 0.9)
        translate([-2, 20, 0])
        square([4, 15]);
    }

    // Hinge â€œearsâ€
    difference() {
        union() {
            translate([-14, 34, 0])
            rotate([90, 0, 0])
            cylinder(h = 12, d1 = 10, d2 = 14, $fn = 20);
            mirror([1, 0, 0])
            translate([-14, 34, 0])
            rotate([90, 0, 0])
            cylinder(h = 12, d1 = 10, d2 = 14, $fn = 20);
        }
        // Hollow hinge to save material
        translate([-10, 34, 0])
        rotate([90, 0, 0])
        cylinder(h = 14, d = 5, $fn = 12);
        mirror([1, 0, 0])
        translate([-10, 34, 0])
        rotate([90, 0, 0])
        cylinder(h = 14, d = 5, $fn = 12);
    }

    // Growth lines (thin concentric ridges)
    for (r = [10:5:30]) {
        difference() {
            scale([1, 0.85, 0.4])
            translate([0, 0, 0.2])
            sphere(d = 70 + r * 2, $fn = 120);
            scale([1, 0.85, 0.4])
            sphere(d = 70 + r * 2 - 0.5, $fn = 120);
        }
    }

    // Tiny food texture dots
    for (i = [0:30]) {
        rotate([0, 0, rands(0, 360, 1)[0]])
        translate([rands(8, 30, 1)[0], rands(0, 25, 1)[0], 0.5])
        sphere(d = 0.8, $fn = 8);
    }
}

