module cheese_wheel() {
    difference() {
        // Main cheese body
        color(color_rind) {
            cylinder(d = wheel_d, h = wheel_h, $fn = 64);
        }

        // Top and bottom rind removal
        translate([0, 0, -0.1]) {
            color(color_cheese) {
                cylinder(d = wheel_d - rind_thick*2, h = wheel_h + 0.2, $fn = 64);
            }
        }

        // Side rind removal
        color(color_cheese) {
            cylinder(d = wheel_d - rind_thick*2, h = wheel_h, $fn = 64);
        }
    }

    // Add rind details on top/bottom edges
    for (i = [0:5]) {
        rotate([0, 0, i * 60]) {
            translate([wheel_d/2 - rind_thick/2, 0, wheel_h]) {
                color(color_rind) {
                    cube([rind_thick, 3, 1], center = true);
                }
            }
            translate([wheel_d/2 - rind_thick/2, 0, 0]) {
                color(color_rind) {
                    cube([rind_thick, 3, 1], center = true);
                }
            }
        }
    }
}

module cheese_eyes() {
    // Generate random eyes using golden ratio for distribution
    for (i = [1:eyes_count]) {
        angle = 137.508 * i;
        radius = (wheel_d/2 - eye_max_r*2) * sqrt(i/eyes_count);
        x = cos(angle) * radius;
        y = sin(angle) * radius;
        z = wheel_h/2 + sin(i*2.5) * (wheel_h/4 - eye_max_r);

        // Vary hole sizes
        hole_r = eye_min_r + (eye_max_r - eye_min_r) * (0.3 + 0.7 * (i % 3)/2);

        translate([x, y, z]) {
            color(color_holes) {
                sphere(r = hole_r, $fn = 12);
