            // Slice wedge
            rotate([0, 0, -slice_angle/2]) {
                cube([wheel_d*2, wheel_d*2, slice_w], center = true);
            }

            rotate([0, 0, slice_angle/2]) {
                cube([wheel_d*2, wheel_d*2, slice_w], center = true);
            }
        }
    }
}

