    difference() {
        // Main avocado body
        scale([1, 0.7, 0.5])
            sphere(d = avocado_length);

        // Cut in half
        translate([0, 0, -avocado_height/2])
            cube([avocado_length*2, avocado_width*2, avocado_height], center = true);

        // Hollow out
        scale([1, 0.7, 0.5])
            sphere(d = avocado_length - wall_thickness*2);
    }

    // Inner flesh color
    color([0.4, 0.7, 0.3]) {
        difference() {
            // Flesh
            scale([1, 0.7, 0.5])
                sphere(d = avocado_length - wall_thickness*2);

            // Pit cavity
            translate([0, 0, -avocado_height/4])
                sphere(d = pit_radius*2);
        }
    }

    // Pit
    color([0.5, 0.3, 0.15]) {
        translate([0, 0, -avocado_height/4])
            scale([1, 1, 0.8])
                sphere(d = pit_radius*2);
    }

    // Skin texture
    color([0.2, 0.4, 0.15]) {
        for (i = [0:15]) {
            rotate([0, 0, i*24])
                translate([avocado_length/2 - wall_thickness, 0, 0])
                    scale([1, 0.7, 0.3])
                        sphere(d = 2);
        }
    }

    // Flesh texture
    color([0.5, 0.8, 0.4]) {
        for (i = [0:8]) {
            for (j = [0:4]) {
                theta = i*45;
                r = j*6 + 10;
                x = r * cos(theta);
                y = r * sin(theta) * 0.7;
                z = -5;

                if (sqrt(x*x + y*y) < pit_radius + 5) continue;

                translate([x, y, z])
                    scale([1, 0.7, 0.2])
                        sphere(d = 1);
            }
        }
    }
}

