waffle_h = 8;           // mm
butter_w = 25;          // mm
butter_h = 3;           // mm
syrup_thick = 1.5;      // mm
wall = 1.2;             // mm â€“ enough for 0.4 mm nozzle

// Color constants
waffle_color = [210/255, 180/255, 140/255];
butter_color = [250/255, 240/255, 210/255];
syrup_color  = [150/255,  75/255,   0/255];

module waffle_base() {
    difference() {
        // Main disk
        color(waffle_color)
            cylinder(h = waffle_h, d = waffle_d, $fn = 64);

        // Center hole (light cavity)
        translate([0, 0, -0.01])
            cylinder(h = waffle_h + 0.02, d = waffle_d - 2*wall, $fn = 64);
    }
}

module waffle_grid() {
    // Square grid pattern on top face
    n = 12;               // number of squares across diameter
    step = waffle_d / n;

    for (i = [-n/2:n/2-1])
        for (j = [-n/2:n/2-1]) {
            translate([i*step + step/2, j*step + step/2, waffle_h - 0.5])
                cube([step - 1.2, step - 1.2, 1], center = true);
        }
}

module butter_pats() {
    // Two rectangular pats of butter slightly offset
    for (p = [[-10, 10], [10, -10]]) {
        translate(concat(p, [waffle_h + butter_h/2]))
            color(butter_color)
                cube([butter_w, butter_w*0.7, butter_h], center = true);
    }
}

module syrup_drizzle() {
    // Random syrup paths
    seed = 42;
    n_paths = 6;
    for (i = [1:n_paths]) {
        angle = rands(0, 360, 1, seed + i)[0];
        len   = rands(15, 35, 1, seed + i)[0];
        x = rands(-waffle_d/3, waffle_d/3, 1, seed + i)[0];
        y = rands(-waffle_d/3, waffle_d/3, 1, seed + i)[0];

        translate([x, y, waffle_h + syrup_thick/2])
            rotate([0, 0, angle])
            color(syrup_color)
                cube([len, 2.4, syrup_thick], center = true);
    }
}

union() {
    waffle_base();
    waffle_grid();
    butter_pats();
    syrup_drizzle();
}

