// Main lemon wedge shape
module lemon_wedge() {
    difference() {
        // Outer lemon shape
        color("yellow") {
            // Main lemon body
            intersection() {
                // Cylinder for basic lemon shape
                cylinder(h=height, r=radius, center=true);

                // Cut at 45Â° angle to make wedge
                rotate([0, 45, 0])
                    cube([radius*3, radius*3, radius*3], center=true);
            }

            // Add thickness to the cut face
            translate([0, 0, -height/2])
                cube([radius*2, thickness, height], center=true);
