thickness = 8;
salt_size = 1.2;
salt_amount = 20;

// Main pretzel shape
module pretzel() {
    color([0.82, 0.65, 0.45]) // Golden brown color

    // Left loop
    translate([-15, 0, 0])
    rotate([0, 0, -30])
    torus_segment(thickness, 25, 180);

    // Right loop
    translate([15, 0, 0])
    rotate([0, 0, 30])
    torus_segment(thickness, 25, 180);

    // Bottom arc
    translate([0, -20, 0])
    rotate([0, 0, 180])
    torus_segment(thickness, 25, 180);

    // Cross piece
    hull() {
        translate([-15, 5, 0])
        sphere(thickness/2);
        translate([15, 5, 0])
        sphere(thickness/2);
    }

    // Top connections
    hull() {
        translate([-15, 25, 0])
        sphere(thickness/2);
        translate([-5, 15, 0])
        sphere(thickness/2);
    }

    hull() {
        translate([15, 25, 0])
        sphere(thickness/2);
        translate([5, 15, 0])
        sphere(thickness/2);
    }
}

// Create torus segment for curved parts
module torus_segment(radius, major_r, degrees) {
    rotate_extrude(angle=degrees)
    translate([major_r, 0, 0])
    circle(r=radius);
}

// Salt crystals
module salt() {
    color([0.95, 0.95, 0.9])
    for(i=[1:salt_amount]) {
        // Random positions on pretzel surface
        angle = i * 137.5; // Golden angle
        r = pretzel_diameter/2 * 0.8;
        x = cos(angle) * r * (0.5 + noise(i)/2);
        y = sin(angle) * r * (0.5 + noise(i+salt_amount)/2) - 10;

        translate([x, y, thickness/2 + 0.5])
        rotate([rand(i)*30, rand(i+1)*30, rand(i+2)*30])
        scale([1, 1, 0.5])
        cube(salt_size, center=true);
    }
}

// Simple noise function for variation
function noise(n) = (n*13.7)%1;

// Random number generator
function rand(n) = (n*73.3)%360;

// Add texture lines for realism
