// Unsalted Pretzel - Realistic Style
// Dimensions: ~75mm wide x ~60mm tall

$fa = 2;
$fs = 0.5;

module pretzel() {
    color([0.85, 0.65, 0.45]) {
        // Main pretzel body - twisted loop
        rotate([0, 0, 45]) {
            // Left loop
            for(i = [0:1]) {
                mirror([i, 0, 0]) {
                    translate([0, 0, 5]) {
                        difference() {
                            // Thick loop base
                            rotate_extrude(angle = 180, $fn = 32) {
                                translate([25, 0, 0]) {
                                    circle(r = 4, $fn = 16);
                                }
                            }
                            // Hollow center
                            rotate_extrude(angle = 180, $fn = 32) {
                                translate([25, 0, 0]) {
                                    circle(r = 2.5, $fn = 16);
                                }
                            }
                        }
                    }

                    // Connect loops to center
                    translate([0, 25, 5]) {
                        rotate([90, 0, 90]) {
                            difference() {
                                cylinder(h = 20, r = 4, $fn = 16);
                                cylinder(h = 20, r = 2.5, $fn = 16);
                            }
                        }
                    }
                }
            }

            // Bottom crossover
            translate([0, -10, 5]) {
                rotate([90, 0, 0]) {
                    difference() {
                        cylinder(h = 35, r = 4.5, $fn = 16);
                        cylinder(h = 35, r = 3, $fn = 16);
                    }
                }
            }

            // Top crossover
            translate([0, 10, 5]) {
                rotate([90, 0, 0]) {
                    difference() {
                        cylinder(h = 25, r = 4.5, $fn = 16);
                        cylinder(h = 25, r = 3, $fn = 16);
                    }
                }
            }
        }

        // Pretzel ends - tapered
        for(i = [0:1]) {
            rotate([0, 0, i * 180]) {
                translate([0, 38, 5]) {
                    rotate([0, 90, 0]) {
                        // Tapered end
                        hull() {
                            translate([0, 0, 0]) {
                                sphere(r = 4, $fn = 16);
                            }
                            translate([0, 0, -8]) {
                                sphere(r = 3, $fn = 16);
                            }
                        }
                    }
                }
            }
        }
    }

    // Salt crystal texture (minimal for unsalted)
    color([0.9, 0.75, 0.55]) {
        for(i = [1:20]) {
            rotate([0, 0, i * 18 + rand(i) * 10]) {
                translate([rand(i) * 60 - 30, rand(i+10) * 60 - 30, rand(i+20) * 8 + 2]) {
                    scale([1, 1, 0.3]) {
                        sphere(r = 0.3 + rand(i+30) * 0.2, $fn = 8);
                    }
                }
            }
        }
    }
}

// Helper function for random values
function rand(seed) = rands(0, 1, 1, seed)[0];

// Create the pretzel
pretzel();

// Cultured Blue Cheese Wheel - realistic style, medium complexity

// Wheel parameters
wheel_d = 60;
wheel_h = 25;
rind_thick = 1.5;
crust_color = "#F5F5DC";
vein_color = "#104E8B";

// Main wheel body with rind
difference() {
    // Full wheel
    union() {
        color(crust_color)
        cylinder(h = wheel_h, d = wheel_d, $fn = 100);

        // Bottom rind lip
        color(crust_color)
        translate([0, 0, -rind_thick/2])
            cylinder(h = rind_thick, d1 = wheel_d, d2 = wheel_d*1.02, $fn = 100);

        // Top rind lip
        color(crust_color)
        translate([0, 0, wheel_h - rind_thick/2])
            cylinder(h = rind_thick, d1 = wheel_d*1.02, d2 = wheel_d, $fn = 100);
    }

    // Hollow out center
    cylinder(h = wheel_h + 2, d = wheel_d - 2*rind_thick, $fn = 100);
}

// Cheese paste (inner part)
translate([0, 0, rind_thick])
    color("#FFF8DC") cylinder(h = wheel_h - 2*rind_thick, d = wheel_d - 2*rind_thick, $fn = 100);

// Blue veins module
module vein(x, y, z, rot) {
    translate([x, y, z]) {
        rotate([0, 0, rot]) {
            color(vein_color, 0.7)
            scale([1, 0.3, 0.3])
                sphere(r = 1.5, $fn = 8);
        }
    }
}

// Generate blue veins network
for (i = [0:8]) {
    angle = i * 45;
    radius = (wheel_d/2 - 5) * 0.7;

    // Radial veins
    vein(
        cos(angle) * radius,
        sin(angle) * radius,
        wheel_h/2,
        angle
    );

    // Additional veins at different heights
    vein(
        cos(angle + 22.5) * radius * 0.8,
        sin(angle + 22.5) * radius * 0.8,
        wheel_h/3,
        angle + 22.5
    );

    vein(
        cos(angle - 22.5) * radius * 0.9,
        sin(angle - 22.5) * radius * 0.9,
        wheel_h*2/3,
        angle - 22.5
    );
}

// Natural cracks
module crack(x, y, z, len, wid) {
    translate([x, y, z]) {
        rotate([0, 0, rands(0, 360, 1)[0]])
        scale([len, wid, 0.5])
            color("#D3D3D3")
            cube([1, 1, 1], center = true);
    }
}

// Surface texture cracks
for (i = [0:12]) {
    angle = rands(0, 360, 1)[0];
    dist = rands(5, wheel_d/2 - 3, 1)[0];
    crack(
        cos(angle) * dist,
        sin(angle) * dist,
        wheel_h - rind_thick - 0.5,
        rands(2, 5, 1)[0],
        rands(0.3, 0.8, 1)[0]
    );
}

// Bottom surface cracks
for (i = [0:10]) {
    angle = rands(0, 360, 1)[0];
    dist = rands(5, wheel_d/2 - 3, 1)[0];
    crack(
        cos(angle) * dist,
        sin(angle) * dist,
        rind_thick + 0.5,
        rands(2, 4, 1)[0],
        rands(0.3, 0.7, 1)[0]
    );
}

// Small mold spots
module mold_spot(x, y, z) {
    translate([x, y, z])
        color("#2F4F4F", 0.8)
        sphere(r = 0.3 + 0.2*sin(x*y), $fn = 6);
}

// Surface mold
for (i = [0:15]) {
    angle = rands(0, 360, 1)[0];
    dist = rands(8, wheel_d/2 - 4, 1)[0];
    height = rands(rind_thick + 1, wheel_h - rind_thick - 1, 1)[0];

    mold_spot(
        cos(angle) * dist,
        sin(angle) * dist,
        height
    );
}

