// Realistic Strained Banana - Medium Complexity
// Features peel, fruit body, and characteristic banana curve

$fn = 50;  // Smooth curves for realism

// Main banana module
module banana() {
    // Constants for banana proportions
    banana_length = 80;
    banana_width = 20;
    peel_thickness = 1.5;

    // Overall banana shape using hull of spheres
    color("FFD700")  // Banana yellow
    union() {
        // Main banana body - series of overlapping spheres
        for(i = [0:5:banana_length]) {
            translate([
                sin(i/15)*15,  // Curvature
                0, 
                cos(i/15)*15 - 15
            ]) {
                sphere(banana_width/2 - i/20);  // Tapering ends
            }
        }

        // Peel segments
        for(i = [0:3]) {
            rotate([0, 0, i*90])
            translate([0, 0, banana_width/2 - 2])
            peel_segment(banana_length, banana_width);
        }

        // Stem at top
        translate([0, 0, banana_width/2])
        rotate([90, 0, 0])
        cylinder(h=8, r1=4, r2=3);
    }

    // Inner fruit (slightly smaller than peel)
    color("FFF8DC")  // Banana flesh color
    translate([0, 0, -1])
    for(i = [0:5:banana_length-10]) {
        translate([
            sin(i/15)*15,  // Follows same curvature
            0, 
            cos(i/15)*15 - 15
        ]) {
            sphere(banana_width/2 - 3 - i/20);  // Smaller than peel
        }
    }
}

// Individual peel segment
module peel_segment(length, width) {
    peel_thickness = 1.5;

    difference() {
        // Main peel segment
        hull() {
            translate([-length/2, -width/4, 0])
            sphere(width/3);
            translate([length/2, -width/4, 0])
            sphere(width/3);
        }

        // Remove inner part for thickness
        offset = 2;
        hull() {
            translate([-length/2 + offset, -width/4, 0])
            sphere(width/3 - peel_thickness);
            translate([length/2 - offset, -width/4, 0])
            sphere(width/3 - peel_thickness);
        }
    }
}

// Surface texture details
module banana_texture() {
    color("8B7355")  // Brown spots color
    for(i = [0:20]) {
        rotate([0, 0, i*18])
        translate([
            sin(i*3)*15, 
            cos(i*3)*15, 
            sin(i*5)*5
        ]) {
            sphere(0.5 + sin(i*7)*0.3);
        }
    }
}

// Assembly
rotate([0, 90, 0])  // Lay banana on its side
translate([0, 0, 10])
union() {
    banana();
    banana_texture();
}

