// Icelandic Pylsa (hot dog) with all traditional toppings
$fn = 32;

// Colors
$mustard = [0.8, 0.7, 0.1];
$brown = [0.6, 0.3, 0.15];
$red = [0.7, 0.1, 0.1];
$white = [0.95, 0.9, 0.85];
$light_brown = [0.85, 0.7, 0.5];

// Main parameters
length = 80;
dog_diameter = 18;
bun_diameter = 22;
wall_thickness = 2;

// Module for hot dog
module hot_dog() {
    color($brown) {
        // Main sausage
        translate([0, 0, 0])
            cylinder(h = length, d = dog_diameter);

        // Slightly tapered ends
        translate([length/2 + 5, 0, 0])
            sphere(d = dog_diameter * 0.8);
        translate([-length/2 - 5, 0, 0])
            sphere(d = dog_diameter * 0.8);
    }
}

// Module for bun with realistic texture
module bun() {
    color($light_brown) {
        // Main bun body - split top
        difference() {
            // Bottom half
            translate([0, 0, -bun_diameter/2])
                cube([length + 20, bun_diameter * 1.8, bun_diameter], center = true);

            // Top cut - slightly curved
            translate([0, 0, -bun_diameter/4])
                cylinder(h = length + 25, d = bun_diameter * 1.9);
        }

        // Top half
        difference() {
            translate([0, 0, bun_diameter/2])
                cube([length + 20, bun_diameter * 1.8, bun_diameter], center = true);

            // Hollow for hot dog
            translate([0, 0, bun_diameter/2])
                cylinder(h = length + 10, d = dog_diameter + 4);

            // Bottom cut
            translate([0, 0, bun_diameter * 0.75])
                cylinder(h = length + 25, d = bun_diameter * 1.9);
        }

        // Sesame seeds
        color($white) {
            for(i = [0:15]) {
                for(j = [-3:3]) {
                    translate([
                        -length/2 + i * 5 + (j%2)*2.5,
                        j * 3,
                        bun_diameter * 0.9
                    ]) {
                        scale([1, 1, 0.5])
                            sphere(d = 1.5);
                    }
                }
            }
        }
    }
}

// Module for crispy onions
module crispy_onions() {
    color($white) {
        for(i = [0:30]) {
            translate([
                -length/2 + i * 2.5,
                random(-5, 5),
                bun_diameter/2 + 2
            ]) {
                rotate([random(0, 180), random(0, 180), 0])
                    scale([1, 0.5, 0.2])
                        sphere(d = 2 + random(0, 1));
            }
        }
    }
}

// Module for fried onions
module fried_onions() {
    color($brown) {
        for(i = [0:20]) {
            translate([
                -length/2 + i * 3.5,
                random(-3, 3),
                bun_diameter/2 + 1
            ]) {
                rotate([random(0, 180), random(0, 180), 0])
                    scale([1, 0.8, 0.3])
                        sphere(d = 1.5 + random(0, 0.5));
            }
        }
    }
}

// Module for ketchup
