pastry_color = "#D4A574";
golden_brown = "#B8956A";

// Main module
module banitsa() {
    // Base pastry layers
    union() {
        // Bottom spiral base
        spiral_layers(8, 2);

        // Top cheese filling visible between layers
        cheese_filling();

        // Top spiral layers
        translate([0, 0, 12]) spiral_layers(6, 1.5);

        // Surface texture - phyllo layers
        surface_texture();
    }
}

// Spiral pastry layers
module spiral_layers(turns, height) {
    for (i = [0: turns*10]) {
        angle = i * 36;
        radius = 40 - i * 2;
        if (radius > 5) {
            translate([cos(angle) * radius, sin(angle) * radius, 0]) {
                rotate([0, 0, angle]) {
                    color(pastry_color) 
                    linear_extrude(height) 
                    offset(r = 0.5) 
                    square([3, 15], center = true);
                }
            }
        }
    }
}

// Cheese filling between layers
module cheese_filling() {
    color(cheese_color) {
        // Random cheese chunks
        for (i = [0:30]) {
            translate([
                (rand(i*1.7) - 0.5) * 70,
                (rand(i*2.3) - 0.5) * 70,
                5 + rand(i*3.1) * 8
            ]) {
                scale([1, 1, 0.3]) 
                sphere(d = 3 + rand(i*4.7) * 6);
            }
        }
    }
}

// Surface texture - phyllo dough pattern
module surface_texture() {
    color(golden_brown) {
        // Crispy top layer texture
        for (r = [10:10:40]) {
            rotate_extrude() {
                translate([r, 0, 0]) {
                    for (i = [0:5:360]) {
                        translate([0, 0, 23]) {
                            rotate([90, 0, i]) {
                                cube([0.5, 2, 0.2], center = true);
                            }
                        }
                    }
                }
            }
        }

        // Edge details
        difference() {
            cylinder(h = 25, r = 40, center = true);
            cylinder(h = 26, r = 38, center = true);
        }
    }
}

