// Main croissant body
module croissant_body() {
    // Crescent base shape
    rotate([90, 0, 0])
    linear_extrude(height = 30, center = true, twist = -30)
    offset(r = 5)
    offset(r = -5)
    difference() {
        circle(d = 70);
        translate([-30, 0, 0])
        circle(d = 50);
    }
}

// Flaky layers texture
module flaky_layers() {
    for (i = [0:5:25]) {
        color("wheat")
        translate([0, 0, i - 12])
        scale([1 - i/100, 1 - i/100, 0.8])
        croissant_body();
    }
}

// Outer curved ridge
module outer_ridge() {
    rotate([90, 0, 0])
    translate([0, 0, -15])
    rotate_extrude(angle = 180)
    translate([35, 0, 0])
    circle(d = 8);
}

// Inner curve detail
module inner_curve() {
    rotate([90, 0, 0])
    translate([-15, 0, -12])
    rotate_extrude(angle = 180)
    translate([25, 0, 0])
    circle(d = 5);
}

// End cap details
module end_caps() {
    // Left end
    translate([-40, 0, 0])
    rotate([0, 90, 0])
    cylinder(h = 15, d1 = 12, d2 = 8);

    // Right end
    translate([40, 0, 0])
    rotate([0, 90, 0])
    cylinder(h = 15, d1 = 8, d2 = 12);
