// Surface texture - small air pockets
module air_pockets() {
    for (i = [0:20]) {
        angle = i * 18;
        radius = 30 + sin(i * 3) * 10;
        x = cos(angle) * radius;
        y = sin(angle) * radius;
        z = sin(i) * 5;

        translate([x, y, z])
        sphere(d = 2 + sin(i) * 1);
    }
}

// Golden brown color variation
module color_variation() {
    difference() {
        union() {
            color("goldenrod")
            croissant_body();

            color("burlywood")
            scale([0.95, 0.95, 0.95])
            croissant_body();
        }

        color("wheat")
        scale([0.9, 0.9, 0.9])
        croissant_body();
    }
}

// Assembly
