    // Main body with layers
    flaky_layers();

    // Structural details
    color("goldenrod")
    union() {
        outer_ridge();
        inner_curve();
        end_caps();
    }

    // Surface texture
    color("navajowhite")
    intersection() {
        air_pockets();
        scale([1.1, 1.1, 1.1])
        croissant_body();
    }

