    translate([0, 0, -18])
    color("goldenrod")
    cube([90, 40, 1], center = true);
}

// Classic Margherita Pizza â€“ 3D-printable, medium-complexity
// Total print size â‰ˆ 95 mm diameter Ã— 8 mm thick (easy FDM print)

$fa = 1; $fs = 1;

// --- Colors ---
bread = "#f2b46d";
sauce = "#b22222";
cheese = "#ffeb99";
crust = "#d4a76a";
basil = "#006400";

// --- Main Pizza ---
module pizza() {
    // Base crust disc
    cylinder(h = 2, d = 95, center = true);

    // Raised crust ring (no overhangs)
    difference() {
        cylinder(h = 4, d = 95, center = true);
        translate([0, 0, -1])
            cylinder(h = 5, d = 85, center = true);
    }

    // Tomato sauce layer
    color(sauce)
    translate([0, 0, 2.1])
        cylinder(h = 0.7, d = 83, center = true);

    // Mozzarella layer (slightly irregular)
    color(cheese)
    translate([0, 0, 2.9])
        cylinder(h = 0.8, d = 81, center = true);
}

// --- Pepperoni Slices ---
module pepperoni() {
    color("#c62828")
    for(a = [20:37:340])
        translate([sin(a) * 30, cos(a) * 30, 3.8])
            scale([1, 1, 0.4])
                sphere(d = 8);
}

// --- Basil Leaves ---
module basil_leaf(pos, angle) {
    color(basil)
    translate(pos)
    rotate([0, 0, angle])
    scale([1, 0.6, 0.2])
        sphere(d = 8);
}
module basil_leaves() {
    for(a = [0:60:300])
        basil_leaf([sin(a) * 35, cos(a) * 35, 3.8], a);
    basil_leaf([10, -25, 3.8], 30);
}

// --- Crust Texture Dots ---
module crust_dots() {
    color(crust)
    for(r = [42.5:5:47.5])
        for(a = [0:15:359])
            translate([sin(a) * r, cos(a) * r, 2.1])
                sphere(d = 1.5);
}

// --- Assembly ---
union() {
    color(bread) pizza();
    pepperoni();
    basil_leaves();
    crust_dots();
}

// Dutch Stroopwafel (realistic style, medium complexity)
// Diameter: 80mm, Thickness: 10mm

$fn = 64;

// Main stroopwafel module
module stroopwafel() {
    // Outer waffle disk
    color("#B8860B") 
    difference() {
        cylinder(h=10, d=80);

        // Center hole (typical stroopwafel feature)
        translate([0,0,-1])
            cylinder(h=12, d=15);

        // Waffle pattern - outer ring
        for(i=[0:30:360]) {
            rotate([0,0,i]) {
                translate([25,0,9])
                    cube([50,3,2], center=true);
            }
        }

        // Waffle pattern - inner ring
        for(i=[15:30:360]) {
            rotate([0,0,i]) {
                translate([15,0,9])
                    cube([30,2,2], center=true);
            }
        }
    }

    // Waffle grid pattern on top
    color("#D2691E") {
        // Radial lines
        for(i=[0:15:360]) {
            rotate([0,0,i]) {
                translate([0,0,9.5])
                    cube([80,1.5,1], center=true);
            }
        }

        // Concentric circles
        for(r=[20,30,40]) {
            translate([0,0,9.5])
                cylinder(h=1, d=r, center=true);
        }
    }

    // Caramel syrup layer visible at the edge
    color("#8B4513") 
    translate([0,0,5])
        difference() {
            cylinder(h=0.5, d=79.5);
            cylinder(h=1, d=15);
        }

    // Bottom waffle (mirrored top)
    color("#B8860B") 
    mirror([0,0,1])
    translate([0,0,-10]) {
        difference() {
            cylinder(h=10, d=80);
            translate([0,0,-1])
                cylinder(h=12, d=15);
        }

        color("#D2691E") {
            for(i=[0:15:360]) {
                rotate([0,0,i]) {
                    translate([0,0,0.5])
                        cube([80,1.5,1], center=true);
                }
            }

            for(r=[20,30,40]) {
                translate([0,0,0.5])
                    cylinder(h=1, d=r, center=true);
            }
        }
    }

    // Small air bubbles in caramel for realism
    color("#A0522D")
    for(i=[0:8]) {
        rotate([0,0,i*45+22.5]) {
            translate([30,0,5])
                sphere(d=1.5);
        }
    }
}

// Render the stroopwafel
stroopwafel();

// Danish pastry - a classic wienerbrÃ¸d with custard filling and icing
// Realistic style with medium complexity
// Size: 80mm diameter, 25mm height

module danish_pastry() {
    color("#E8B36B") // Golden baked pastry color

    // Base spiral pastry layers
    union() {
        // Bottom layer
        difference() {
            cylinder(h=4, r=40, $fn=64);
            cylinder(h=4.5, r=35, $fn=64);
        }

        // Middle spiral layer
        rotate([0, 0, 15])
        difference() {
            cylinder(h=4, r=35, $fn=64);
            cylinder(h=4.5, r=30, $fn=64);
        }

        // Top spiral layer
        rotate([0, 0, 30])
        difference() {
            cylinder(h=4, r=30, $fn=64);
            cylinder(h=4.5, r=25, $fn=64);
        }
    }

    // Custard filling in center
    color("#FFF8DC") // Custard color
    translate([0, 0, 8])
    cylinder(h=8, r=20, $fn=32);

    // Icing drizzles
    color("#FFFFFF") // White icing
    module icing_drizzle(x, y, angle) {
        translate([x, y, 16])
        rotate([0, 0, angle])
        scale([1, 0.2, 1])
        cylinder(h=2, r=15, $fn=16);
    }

    for(i = [0:45:315]) {
        icing_drizzle(
            25 * cos(i),
            25 * sin(i),
            i + 90
        );
    }

    // Almond slivers on top
    color("#D2B48C") // Almond color
    module almond(x, y, angle) {
        translate([x, y, 18])
        rotate([0, 0, angle])
        scale([0.8, 0.15, 0.1])
        cube([8, 2, 1]);
    }

    for(i = [0:30:330]) {
        r = 18;
        almond(
            r * cos(i),
            r * sin(i),
            i
        );
    }

    // Pastry texture - small bumps on surface
    color("#D4A574")
    module pastry_bump(x, y) {
        translate([x, y, 11.5])
        sphere(r=1.2, $fn=8);
    }

    for(x = [-35:7:35]) {
        for(y = [-35:7:35]) {
            if(norm([x, y]) < 32 && norm([x, y]) > 22) {
                pastry_bump(x, y);
            }
        }
    }

    // Bottom flattening for printing
    color("#C19A6B")
    translate([0, 0, -0.5])
    cylinder(h=1, r=41, $fn=64);
}

// Render the danish
danish_pastry();

// Greek-style Spanakopita (Spinach & Feta Pie) â€“ realistic medium-complexity model
// outer shell: ~75 mm long, 45 mm wide, 25 mm high

$fn = 48;

// MAIN PIE BODY -------------------------------------------------------------

module pie_body() {
    difference() {
        // overall pie shape
        translate([0, 0, 12.5])
            scale([1, 0.6, 0.5])
            sphere(d = 75);

        // hollow inside for filling
        translate([0, 0, 12.5])
            scale([0.9, 0.5, 0.45])
            sphere(d = 75);
    }
}

// PHYLLO LAYERS -------------------------------------------------------------
module phyllo_layers() {
    color("wheat")
    for (i = [0:1:4]) {
        translate([0, 0, 5.5 + i * 0.8])
            scale([1, 0.6, 0.08])
            sphere(d = 75);
    }
}

// SPINACH-FETA FILLING ------------------------------------------------------
module filling() {
    color("darkolivegreen")
    translate([0, 0, 9.5])
        scale([0.9, 0.5, 0.4])
        sphere(d = 75);
}

// TOP CRUST LATTICE ---------------------------------------------------------
module lattice_strips() {
    color("wheat")
    for (y = [-15:5:15]) {
        translate([0, y, 22])
            rotate([0, 0, 45])
            cube([75, 1.2, 0.8], center = true);
    }
    for (x = [-30:5:30]) {
        translate([x, 0, 22])
            rotate([0, 0, -45])
            cube([75, 1.2, 0.8], center = true);
    }
}

// SEEDS ---------------------------------------------------------------------
module sesame_seeds() {
    color("cornsilk")
    for (i = [0:1:50]) {
        translate([rands(-30, 30, 1)[0],
                   rands(-15, 15, 1)[0],
                   22.8 + rands(-0.2, 0.2, 1)[0]])
            sphere(d = 1.2);
    }
}

// ASSEMBLY ------------------------------------------------------------------

union() {
    phyllo_layers();
    filling();
    pie_body();
    lattice_strips();
    sesame_seeds();
}

// Icelandic Pylsa (Hot Dog) with authentic toppings
// Size: ~80mm long, designed for 3D printing

$fn = 32;

// Main colors
color_beige = [0.95, 0.85, 0.75];
color_brown = [0.6, 0.35, 0.2];
color_red = [0.8, 0.2, 0.2];
color_white = [0.95, 0.95, 0.9];
color_green = [0.3, 0.6, 0.3];
color_yellow = [0.9, 0.8, 0.5];

// Scale factor for printing
scale_factor = 1;

// Main hot dog module
module hot_dog() {
    scale([scale_factor, scale_factor, scale_factor]) {
        union() {
            // Bun bottom
            color(color_beige)
            translate([0, 0, 5])
            difference() {
                scale([1, 0.4, 0.5])
                sphere(r = 15);

                // Bun cutout for sausage
                translate([0, -10, 5])
                scale([0.8, 0.3, 0.8])
                sphere(r = 15);
            }

            // Bun top
            color(color_beige)
            translate([0, 0, 18])
            difference() {
                scale([1, 0.4, 0.4])
                sphere(r = 15);

                // Slight indentation
                translate([0, -8, 2])
                scale([0.9, 0.3, 0.3])
                sphere(r = 15);
            }

            // Sausage
            color(color_brown)
            translate([0, -5, 12])
            scale([1, 0.3, 1])
            rotate([90, 0, 0])
            cylinder(h = 60, r1 = 6, r2 = 6, center = true);

            // Sausage texture lines
            for(i = [-20:4:20]) {
                color([0.5, 0.3, 0.15])
                translate([i, -5, 12])
                rotate([90, 0, 0])
                cylinder(h = 62, r1 = 0.5, r2 = 0.5, center = true);
            }

            // Crispy onions
            module crispy_onion() {
                color(color_yellow)
                translate([0, 0, 0])
                scale([0.7, 0.1, 0.7])
                sphere(r = 2);
            }

            // Add crispy onions
            for(i = [-15:3:15]) {
                translate([i, -3, 18])
                crispy_onion();
            }

            // Raw onions
            module raw_onion() {
                color(color_white)
                translate([0, 0, 0])
                scale([0.8, 0.2, 0.8])
                cube([3, 1, 3], center = true);
            }

            // Add raw onions
            for(i = [-12:4:12]) {
                translate([i, -2, 19.5])
                raw_onion();
            }

            // Mustard
            color(color_yellow)
            translate([0, -5, 20])
            rotate([90, 0, 0])
            cylinder(h = 60, r1 = 2, r2 = 2, center = true);

            // Ketchup
            color(color_red)
            translate([0, -5, 21])
            rotate([90, 0, 0])
            cylinder(h = 60, r1 = 1.5, r2 = 1.5, center = true);

            // Remoulade sauce
            color(color_white)
            translate([0, -5, 22])
            rotate([90, 0, 0])
            cylinder(h = 60, r1 = 1.8, r2 = 1.8, center = true);

            // Dill garnish
            module dill_leaf() {
                color(color_green)
                translate([0, 0, 0])
                scale([0.3, 1, 0.1])
                rotate([0, 30, 0])
                sphere(r = 3);
            }

            // Add dill pieces
            for(i = [-8:4:8]) {
                translate([i, 0, 22])
                rotate([0, 0, i * 10])
                dill_leaf();
            }
        }
    }
}

// Create the complete Icelandic pylsa
hot_dog();

// Realistic Cake - Medium Complexity
// A three-layer chocolate cake with chocolate frosting and sprinkles

// Color definitions
$fn = 48;

// Main cake module
module cake() {
    // Cake layers - chocolate brown
    color([0.36, 0.25, 0.20]) {
        // Bottom layer - 60mm diameter, 12mm height
        cylinder(h = 12, d = 60, $fn = 64);

        // Middle layer - 50mm diameter, 10mm height
        translate([0, 0, 12])
            cylinder(h = 10, d = 50, $fn = 64);

        // Top layer - 40mm diameter, 8mm height
        translate([0, 0, 22])
            cylinder(h = 8, d = 40, $fn = 64);
    }

    // Frosting between layers - lighter brown
    color([0.50, 0.35, 0.28]) {
        // Frosting layer between bottom and middle
        translate([0, 0, 12])
            cylinder(h = 1.5, d = 60, $fn = 64);

        // Frosting layer between middle and top
        translate([0, 0, 22])
            cylinder(h = 1.5, d = 50, $fn = 64);
    }

    // Top frosting dome
    color([0.50, 0.35, 0.28]) {
        translate([0, 0, 30.5])
            scale([1, 1, 0.6])
                sphere(d = 40);
    }

    // Frosting drips down the sides
    color([0.50, 0.35, 0.28]) {
        for (i = [0:30:330]) {
            rotate([0, 0, i])
                translate([25, 0, 25])
                    cylinder(h = 5 + (i % 15), d1 = 4, d2 = 6);
        }
    }

    // Side frosting decorations
    color([0.50, 0.35, 0.28]) {
        for (i = [0:30:330]) {
            rotate([0, 0, i])
                translate([28, 0, 15])
                    scale([1, 0.7, 1])
                        sphere(d = 6);
        }
    }

    // Chocolate sprinkles on top
    color([0.20, 0.12, 0.08]) {
        for (i = [0:15:345]) {
            for (j = [8:8:16]) {
                rotate([0, 0, i])
                    translate([j, 0, 32])
                        rotate([90, 0, 90])
                            cylinder(h = 3, d = 1.2);
            }
        }
    }

    // Colored sprinkles
    for (i = [0:25:350]) {
        for (j = [5:10:20]) {
            // Random colors for sprinkles
            color([
                0.8 + (i % 20) / 100,
                0.4 + (j % 30) / 100,
                0.6 + (i % 15) / 100
            ]) {
                rotate([0, 0, i + j])
                    translate([j + 2, 0, 33.5])
                        rotate([90, 0, 90])
                            cylinder(h = 2.5, d = 1);
            }
        }
    }
}

// Render the cake
cake();

// Realistic Pie Model - OpenSCAD
// Features: Fluted crust, lattice top, vent holes, golden-brown coloring

// Pie dimensions
pie_diameter = 80;
pie_height = 25;
crust_thickness = 2.5;
wall_height = 12;

// Colors
crust_color = "#D4A574";
filling_color = "#8B4513";
golden_color = "#C68642";

// Main pie module
module pie() {
    // Bottom crust
    union() {
        // Base crust disk
        color(crust_color)
        translate([0, 0, 0])
            cylinder(h=crust_thickness, d=pie_diameter, $fn=64);

        // Outer crust wall with fluted edge
        color(crust_color)
        translate([0, 0, crust_thickness])
            fluted_wall();

        // Filling
        color(filling_color)
        translate([0, 0, crust_thickness])
            cylinder(h=wall_height*0.7, d=pie_diameter-crust_thickness*2, $fn=64);

        // Lattice top crust
        color(golden_color)
        translate([0, 0, crust_thickness+wall_height*0.7])
            lattice_top();

        // Decorative edge
        color(golden_color)
        translate([0, 0, crust_thickness+wall_height])
            decorative_edge();
    }
}

// Create fluted edge pattern on crust wall
module fluted_wall() {
    difference() {
        // Main wall cylinder
        cylinder(h=wall_height, d=pie_diameter, $fn=64);

        // Hollow center
        translate([0, 0, -0.1])
            cylinder(h=wall_height+0.2, d=pie_diameter-crust_thickness*2, $fn=64);

        // Create fluted pattern
        for(i=[0:35]) {
            rotate([0, 0, i*10])
                translate([pie_diameter/2-crust_thickness, 0, 0])
                    cube([crust_thickness*2, 3, wall_height], center=true);
        }
    }
}

// Create lattice pattern on top
module lattice_top() {
    difference() {
        // Main top disk
        translate([0, 0, 0])
            cylinder(h=crust_thickness, d=pie_diameter-crust_thickness*2, $fn=64);

        // Vent holes
        for(i=[-1:1]) {
            for(j=[-1:1]) {
                translate([i*15, j*15, -0.1])
                    cylinder(h=crust_thickness+0.2, d=3, $fn=16);
            }
        }

        // Lattice cuts - horizontal
        for(i=[-30:5:30]) {
            translate([0, i, -0.1])
                cube([pie_diameter-crust_thickness*4, 2, crust_thickness+0.2]);
        }

        // Lattice cuts - vertical
        for(i=[-30:5:30]) {
            rotate([0, 0, 90])
                translate([0, i, -0.1])
                cube([pie_diameter-crust_thickness*4, 2, crust_thickness+0.2]);
        }
    }
}

// Decorative crimped edge
module decorative_edge() {
    difference() {
        // Outer ring
        cylinder(h=crust_thickness, d=pie_diameter, $fn=64);

        // Remove inner part
        translate([0, 0, -0.1])
            cylinder(h=crust_thickness+0.2, d=pie_diameter-crust_thickness*2, $fn=64);

        // Create scalloped edge
        for(i=[0:23]) {
            rotate([0, 0, i*15+7.5])
                translate([pie_diameter/2, 0, 0])
                    rotate([0, 90, 0])
                        cylinder(h=crust_thickness*2, d=6, $fn=8);
        }
    }
}

// Generate the pie
pie();

// Chocolate chip cookie with realistic texture and chips
// Diameter: 70mm, Thickness: 8mm

$fn = 36;

// Main cookie base
module cookie_base() {
    color("#D4A574") {
        difference() {
            // Main disc shape
            cylinder(h = 8, r = 35, center = true);

            // Slight indent on top for realistic cookie shape
            translate([0, 0, 3.5])
                cylinder(h = 2, r = 32, center = true);

            // Slight indent on bottom
            translate([0, 0, -3.5])
                cylinder(h = 2, r = 32, center = true);
        }
    }
}

// Single chocolate chip
module chocolate_chip() {
    color("#2C1810") {
        scale([1, 1, 0.7])
            sphere(r = 2.5);
    }
}

// Surface texture - small bumps for cookie texture
module surface_bumps() {
    color("#C19660") {
        for (i = [0:7]) {
            for (j = [0:15]) {
                angle = i * 45;
                radius = 30 - j * 1.5;
                x = radius * cos(angle);
                y = radius * sin(angle);

                translate([x, y, 4])
                    sphere(r = 0.8);
            }
        }
    }
}

// Edge texture - irregular bumps
module edge_bumps() {
    color("#C19660") {
        for (i = [0:35]) {
            angle = i * 10 + (i % 3) * 3;
            x = 35 * cos(angle);
            y = 35 * sin(angle);
            bump_size = 1.2 + (i % 3) * 0.3;

            translate([x, y, 0])
                sphere(r = bump_size);
        }
    }
}

// Chocolate chips distribution
module chocolate_chips() {
    // Top chips
    for (i = [0:14]) {
        angle = i * 137.5; // Golden angle for natural distribution
        radius = 8 + (i % 3) * 10 + (i * 2);
        x = radius * cos(angle);
        y = radius * sin(angle);

        // Only place chips within cookie area
        if (sqrt(x*x + y*y) < 30) {
            translate([x, y, 4.5])
                rotate([90, 0, angle])
                chocolate_chip();
        }
    }

    // Bottom chips (fewer)
    for (i = [0:7]) {
        angle = i * 137.5 + 180;
        radius = 8 + (i % 2) * 12;
        x = radius * cos(angle);
        y = radius * sin(angle);

        if (sqrt(x*x + y*y) < 30) {
            translate([x, y, -4.5])
                rotate([90, 0, angle])
                mirror([0, 0, 1])
                chocolate_chip();
        }
    }

    // Side chips
    for (i = [0:5]) {
        angle = i * 60 + 30;
        x = 33 * cos(angle);
        y = 33 * sin(angle);

        translate([x, y, 0])
            rotate([0, 90, angle + 90])
            scale([0.8, 0.8, 1])
            chocolate_chip();
    }
}

// Bake marks - darker spots
module bake_marks() {
    color("#8B5A2B") {
        for (i = [0:9]) {
            angle = i * 36 + (i % 2) * 18;
            radius = 15 + (i % 2) * 8;
            x = radius * cos(angle);
            y = radius * sin(angle);

            translate([x, y, 4.1])
                scale([1, 1, 0.1])
                sphere(r = 2 + (i % 3));
        }
    }
}

// Assemble the complete cookie
union() {
    cookie_base();
    surface_bumps();
    edge_bumps();
    chocolate_chips();
    bake_marks();
}

// Licorice Allsorts - realistic style, medium complexity
