    color(color_black) {
        translate([-35, -10, 0]) {
            cube([70, 20, 5]);
        }
    }

    // White fondant layer with sugar texture
    color(color_white) {
        translate([-35, -10, 5]) {
            difference() {
                cube([70, 20, 4]);
                // Sugar crystal texture on top
                for(x = [-34:4:34]) {
                    for(y = [-9:4:9]) {
                        translate([x, y, 3.8]) {
                            cylinder(h=0.5, d=1.2, $fn=8);
                        }
                    }
                }
            }
    }

    // Pink fondant layer
    color(color_pink) {
        translate([-35, -10, 9]) {
            cube([70, 20, 3]);
        }
    }

    // Yellow fondant layer
    color(color_yellow) {
        translate([-35, -10, 12]) {
            cube([70, 20, 2]);
        }
    }

    // Orange decorative layer with holes
    color(color_orange) {
        translate([-35, -10, 14]) {
            difference() {
                cube([70, 20, 1]);
                // Create patterned holes
                for(i = [-30:10:30]) {
                    translate([i, 0, 0]) {
                        cylinder(h=2, d=6, $fn=6);
                    }
                }
            }
        }
    }

    // Rounded edges for realistic appearance
    color(color_black) {
        // Front and back rounded edges
        for(x = [-35:2:35]) {
            translate([x, -10, 2.5]) {
                rotate([90, 0, 0]) {
                    cylinder(h=2, d=5, $fn=12);
                }
