// Create chocolate bar with segments
module chocolate_bar() {
    color(chocolate_color) {
        difference() {
            // Main bar body with rounded top edges
            rounded_bar();

            // Create segment grooves
            for (i = [1:segment_cols-1]) {
                translate([(i * bar_length/segment_cols) - segment_gap/2, 0, bar_thickness - segment_depth])
                    cube([segment_gap, bar_width, segment_depth + 0.1]);
            }
            for (j = [1:segment_rows-1]) {
                translate([0, (j * bar_width/segment_rows) - segment_gap/2, bar_thickness - segment_depth])
                    cube([bar_length, segment_gap, segment_depth + 0.1]);
            }

            // Add logo impression on center segment
            translate([bar_length/2 - 12, bar_width/2 - 6, bar_thickness - logo_depth])
                rotate([0, 0, 45])
                scale([1, 0.7, 1])
                cylinder(h = logo_depth + 0.1, r1 = 4, r2 = 4, $fn = 30);
        }

        // Add subtle texture dots
        for (x = [5:10:bar_length-5]) {
            for (y = [5:10:bar_width-5]) {
                translate([x, y, bar_thickness - 0.3])
                    sphere(r = 0.4);
            }
        }
    }
}

// Create rounded top surface
