    difference() {
        // Base rectangular block
        cube([bar_length, bar_width, bar_thickness]);

        // Round the bottom edges
        translate([-0.1, -0.1, -0.1])
            cube([bar_length + 0.2, bar_width + 0.2, 0.1]);

        // Add rounded top
        translate([bar_length/2, bar_width/2, bar_thickness - corner_radius])
            minkowski() {
                cube([bar_length - corner_radius*2, bar_width - corner_radius*2, corner_radius*2], center = true);
                sphere(r = corner_radius);
