whipped_color = [1, 1, 1];
cherry_color = [0.8, 0.1, 0.1];

// Sundae glass
module sundae_glass() {
    // Base
    difference() {
        cylinder(h=5, r1=glass_diameter/2, r2=glass_diameter/2, $fn=32);
        translate([0, 0, 1])
            cylinder(h=4, r1=glass_diameter/2-3, r2=glass_diameter/2-3, $fn=32);
    }

    // Stem
    cylinder(h=glass_height/3, r1=5, r2=5, $fn=16);

    // Bowl
    difference() {
        cylinder(h=glass_height*2/3, r1=glass_diameter/2, r2=glass_diameter/2-5, $fn=32);
        translate([0, 0, 2])
            cylinder(h=glass_height*2/3-2, r1=glass_diameter/2-4, r2=glass_diameter/2-9, $fn=32);
    }
}

// Ice cream ball with texture
module ice_cream_ball(radius, color_value) {
    color(color_value) {
        // Main sphere
        sphere(r=radius, $fn=32);

        // Add texture with small spheres
        for(i = [0:8]) {
            rotate([0, 0, i*45])
                translate([radius*0.7, 0, radius*0.3])
                    sphere(r=radius*0.12, $fn=8);
        }
        for(i = [0:6]) {
            rotate([0, 0, i*60])
                translate([radius*0.5, 0, radius*0.6])
                    sphere(r=radius*0.1, $fn=8);
        }
    }
}

// Whipped cream swirl
module whipped_cream() {
    color(whipped_color) {
        for(i = [0:5]) {
            rotate([0, 0, i*60])
                translate([i*1.5, 0, i*1.2])
                    cylinder(h=whipped_height-i*0.5, r1=5-i*0.5, r2=3-i*0.3, $fn=16);
        }
    }
}

// Cherry on top
module cherry() {
    color(cherry_color) {
        translate([0, 0, cherry_radius])
            sphere(r=cherry_radius, $fn=16);

        // Stem
        color([0.2, 0.4, 0.1])
            rotate([30, 0, 0])
                cylinder(h=6, r1=0.5, r2=0.3, $fn=8);
    }
}

// Chocolate sauce drizzle
module chocolate_sauce() {
    color(chocolate_color) {
        for(i = [0:12]) {
            rotate([0, 0, i*30])
                translate([glass_diameter/2-5, 0, i*2])
                    cylinder(h=1, r1=1, r2=0.5, $fn=8);
        }
    }
}

// Assemble the sundae
color(glass_color) sundae_glass();

