    color([0.82, 0.71, 0.55]) {
        // Base crust
        cylinder(h=base_height, d=base_diameter, $fn=64);

        // Side crust
        translate([0, 0, base_height/2])
            difference() {
                cylinder(h=base_height+2, d1=base_diameter, d2=base_diameter-4, $fn=64);
                cylinder(h=base_height+3, d=base_diameter-6, $fn=64);
            }

        // Texture on top
        translate([0, 0, base_height-0.5])
            crust_texture();
    }
}

// Cream cheese filling
module filling() {
    color([1, 0.96, 0.9]) {
        // Main filling
        cylinder(h=cheesecake_height, d=base_diameter-6, $fn=64);

        // Slightly domed top
        translate([0, 0, cheesecake_height-2])
            scale([1, 1, 0.3])
                sphere((base_diameter-6)/2, $fn=64);
    }
}

// Individual strawberry slice
