    color([0.85, 0.2, 0.2]) {
        difference() {
            // Main berry body
            scale([0.7, 0.7, 0.3])
                sphere(6, $fn=32);

            // Seeds
            for (i = [0:8]) {
                rotate([0, 0, i * 45])
                    translate([3.5, 0, 0])
                        sphere(0.3, $fn=6);
            }
        }

        // Green leafy top
        color([0.2, 0.6, 0.2]) {
            for (i = [0:5]) {
                rotate([0, 0, i * 72])
                    translate([2, 0, -0.5])
                        scale([0.3, 1.2, 0.1])
                            sphere(1.5, $fn=6);
            }
        }
    }
}

// Strawberry topping
module topping() {
    // Strawberry glaze
    color([0.9, 0.3, 0.3]) {
        translate([0, 0, cheesecake_height-0.5])
            cylinder(h=1, d=base_diameter-8, $fn=64);
    }

    // Individual strawberries
    for (r = [0:2]) {
        for (a = [0:45:360]) {
            rotate([0, 0, a + r * 15])
                translate([
                    r * 8 + 10,
                    0,
                    cheesecake_height + strawberry_height/2
                ])
                    rotate([90, 0, 0])
                        strawberry_slice();
        }
    }
}

// Assembly
module cheesecake() {
    union() {
        crust();
        translate([0, 0, base_height])
            filling();
        topping();

        // Side detail - slight indent for realistic look
        color([0.95, 0.95, 0.95]) {
            translate([0, 0, base_height + cheesecake_height/2])
                cylinder(h=cheesecake_height+2, d=base_diameter-5.5, $fn=64);
        }
    }
}

