// Toffee bar with realistic texture and wrapper impression
// Dimensions: 80mm x 40mm x 15mm

module toffee_bar() {
    // Main toffee body with rounded edges
    color([0.72, 0.52, 0.32])  // caramel brown color

    // Create rounded rectangular bar using hull of spheres
    hull() {
        // Bottom corners
        translate([5, 5, 2]) sphere(3, $fn=16);
        translate([75, 5, 2]) sphere(3, $fn=16);
        translate([5, 35, 2]) sphere(3, $fn=16);
        translate([75, 35, 2]) sphere(3, $fn=16);

        // Top corners
        translate([5, 5, 12]) sphere(3, $fn=16);
        translate([75, 5, 12]) sphere(3, $fn=16);
        translate([5, 35, 12]) sphere(3, $fn=16);
        translate([75, 35, 12]) sphere(3, $fn=16);
    }
}

module wrapper_impression() {
    // Create wrapper seam impression on top surface
    color([0.85, 0.85, 0.85])  // light gray wrapper marks

    // Central seam line
    translate([40, 20, 14.9]) 
        cube([80, 0.5, 0.3], center=true);

    // Cross folds
    for(i = [1:3]) {
        translate([i * 20, 20, 14.9]) 
            cube([0.5, 40, 0.3], center=true);
    }
}

module sugar_crystals() {
    // Random sugar crystals on surface
    color([0.92, 0.92, 0.88])  // off-white crystal color

    for(i = [1:40]) {
        // Random positions across surface
        translate([
            5 + rand(i * 3) * 70, 
            5 + rand(i * 5) * 30, 
            12 + rand(i * 7) * 2
        ]) {
            // Small irregular crystals
            scale([1, 1, 0.5]) sphere(0.3 + rand(i * 11) * 0.4, $fn=6);
        }
    }
}

module broken_edge() {
    // Create realistic broken edge on one corner
    difference() {
        // Full corner
        translate([75, 5, 0]) 
            cube([5, 5, 15]);

        // Remove irregular chunk
        translate([77, 7, 5]) 
            rotate([0, 0, 25]) 
                cube([8, 8, 10]);

        // Add fracture surface detail
        for(i = [1:15]) {
            translate([
                75 + rand(i * 13) * 3, 
                5 + rand(i * 17) * 3, 
                rand(i * 19) * 12
            ]) {
                sphere(0.2 + rand(i * 23) * 0.3, $fn=8);
            }
        }
    }
}

// Helper function for pseudo-random numbers
function rand(seed) = (seed * 9301 + 49297) % 233280 / 233280;

// Assembly with proper positioning
translate([-40, -20, 0]) {
    union() {
        toffee_bar();
        wrapper_impression();
        sugar_crystals();
        broken_edge();
    }
}

// Nougat bar with nuts and rice crisps
// Dimensions: 60mm x 25mm x 15mm

// Main nougat base
module nougat_base() {
    color([0.95, 0.9, 0.85])
    difference() {
        // Soft nougat body
        translate([-30, -12.5, -7.5])
            scale([1, 0.9, 0.8])
            sphere(d=30);

        // Flatten bottom
        translate([-35, -15, -7.5])
            cube([70, 30, 7.5]);

        // Create rectangular bar shape
        translate([-35, -15, -7.5])
            cube([70, 30, 15]);
    }
