// Almond pieces
module almond_piece(x, y, z, angle) {
    color([0.9, 0.8, 0.7])
    translate([x, y, z])
        rotate([0, 0, angle])
        scale([1.5, 0.8, 0.5])
        sphere(d=4);
}

// Hazelnut pieces
module hazelnut_piece(x, y, z, angle) {
    color([0.6, 0.4, 0.2])
    translate([x, y, z])
        rotate([angle, angle*0.5, 0])
        sphere(d=3);
}

// Rice crisps
module rice_crisp(x, y, z) {
    color([0.98, 0.95, 0.92])
    translate([x, y, z])
        scale([0.8, 0.8, 0.5])
        cube([2, 2, 0.5]);
}

// Surface texture details
module surface_texture() {
    for (i = [-25:5:25]) {
        for (j = [-10:5:10]) {
            // Random height variation
            h = (sin(i*0.3) * cos(j*0.3)) * 0.5 + 0.5;
            translate([i, j, 7.5 + h])
                sphere(d=1);
        }
    }
}

// Wrapper edges
module wrapper_edges() {
    color([0.9, 0.85, 0.8], 0.3)
    difference() {
        // Thin wrapper layer
        translate([-31, -13, -8])
            cube([62, 26, 16]);

        // Remove inner part
        translate([-29.5, -11.5, -6.5])
            cube([59, 23, 13]);
    }
}

// Assembly
union() {
    // Main nougat
    nougat_base();

    // Almond pieces on top
    for (i = [-20:10:20]) {
        almond_piece(i, -5, 7.5, i*10);
        almond_piece(i+5, 5, 7.5, i*15);
    }

    // Hazelnut pieces
    for (i = [-15:12:15]) {
        hazelnut_piece(i, -8, 5, i*20);
        hazelnut_piece(i-3, 8, 5, i*25);
    }

    // Rice crisps embedded
    for (i = [-25:8:25]) {
        for (j = [-10:5:10]) {
            rice_crisp(i, j, 3 + (i+j)%3);
        }
    }

    // Surface texture
    surface_texture();

    // Wrapper edges
    wrapper_edges();
}

// Realistic 3D printable marshmallow
// Features: squashed cylinder, rounded ends, puffed texture, slight imperfections

color("Ivory") difference() {
    union() {
        // Main body: cylinder with domed ends
        translate([0, 0, 25])
        scale([1, 1, 0.8])
        minkowski() {
            cylinder(h = 30, r = 25, $fn = 64);
            sphere(5);
        }

        // Top dome
        translate([0, 0, 55])
        scale([1, 1, 0.9])
        sphere(25, $fn = 64);

        // Bottom slightly flattened
        translate([0, 0, 0])
        scale([1, 1, 0.7])
        sphere(25, $fn = 64);
    }

    // Puffy texture using small spheres
    for (i = [0:8]) {
        for (j = [0:8]) {
            // Calculate positions in polar coordinates
            angle = i * 45;
            radius = 20 + j * 1.5;
            x = cos(angle) * radius;
            y = sin(angle) * radius;

            // Top surface dimples
            translate([x, y, 55 + 2.5 - j * 0.5])
            sphere(2.5, $fn = 16);

            // Side surface bumps
            h = 25 + j * 2;
            translate([x * 0.9, y * 0.9, h])
            sphere(1.5, $fn = 16);
        }
    }

    // Bottom flattening
    translate([0, 0, -5])
    cube([60, 60, 10], center = true);

    // Slight imperfections
    for (k = [0:5]) {
        angle = k * 60 + 30;
        x = cos(angle) * 5;
        y = sin(angle) * 5;
        translate([x, y, 40])
        sphere(3 + k * 0.5, $fn = 16);
    }
}

