// Caramel Cube with Flowing Top Surface
// Realistic style, medium complexity caramel candy

color("#C68642")
union() {
    // Main caramel cube body
    difference() {
        // Main cube with rounded edges
        minkowski() {
            cube([42, 42, 35], center = true);
            sphere(3);
        }

        // Hollow center for 3D printing
        translate([0, 0, 5])
            cube([34, 34, 35], center = true);
    }

    // Flowing caramel top layer
    translate([0, 0, 18])
    union() {
        // Central bulge
        scale([1.2, 1.2, 0.8])
            sphere(8);

        // Flowing drips
        module drip(pos, size) {
            translate(pos)
                scale([1, 1, 1.5])
                    sphere(size);
        }

        drip([8, 5, -2], 4);
        drip([-7, -6, 0], 3.5);
        drip([0, 10, 1], 4.5);
        drip([-9, 8, -1], 3);
        drip([5, -9, 0], 3.8);
    }

    // Sugar crystal texture on sides
    module crystal(x, y, z) {
        translate([x, y, z])
            cube([2, 2, 1], center = true);
    }

    // Side A crystals
    for(i = [-20:4:20]) {
        for(j = [-15:5:15]) {
            if(abs(i+j) % 3 == 0) {
                crystal(i, 21, j);
                crystal(i, -21, j);
            }
        }
    }

    // Side B crystals
    for(i = [-20:4:20]) {
        for(j = [-15:5:15]) {
            if(abs(i-j) % 3 == 0) {
                crystal(21, i, j);
                crystal(-21, i, j);
            }
        }
    }

    // Bottom indentations for stacking
    translate([0, 0, -20])
    difference() {
        cube([46, 46, 5], center = true);
        for(x = [-15:15:15]) {
            for(y = [-15:15:15]) {
                translate([x, y, 0])
                    cylinder(h=6, r=4, center=true);
            }
        }
    }

    // Glossy top surface
    translate([0, 0, 25])
    scale([1.1, 1.1, 0.3])
        sphere(15);
}

// Peanut brittle bar - realistic style with medium complexity
// Overall dimensions: 80mm x 40mm x 10mm

module peanut() {
    // Individual peanut half - slightly irregular shape
    color("#D2B48C") 
    union() {
        sphere(d=4.2, $fn=12);
        translate([0.3, 0.2, 0.8]) sphere(d=3.8, $fn=12);
        translate([-0.4, -0.3, 0.6]) sphere(d=3.6, $fn=12);
    }
}

module peanut_cluster(x, y, z, angle) {
    // Cluster of 2-3 peanuts in realistic arrangement
    translate([x, y, z]) rotate([0, 0, angle]) {
        peanut();
        translate([2.8, -1.2, 0.8]) rotate([15, 0, 25]) peanut();
        if ((x + y) % 3 == 0) {
            translate([-2.5, 1.5, 1.2]) rotate([-20, 10, -30]) peanut();
        }
    }
}

module brittle_base() {
    // Main sugar base with irregular edges
    color("#CD853F") {
        // Main rectangular base with rounded edges
        union() {
            // Base rectangle
            translate([-40, -20, 0]) cube([80, 40, 8]);

            // Add slight irregularities to edges
            for (i = [-35:10:35]) {
                translate([i, -20.5, 2]) cube([8, 1.5, 2]);
                translate([i, 19.5, 2]) cube([8, 1.5, 2]);
            }
            for (j = [-15:10:15]) {
                translate([-40.5, j, 2]) cube([1.5, 8, 2]);
                translate([39.5, j, 2]) cube([1.5, 8, 2]);
            }
        }

        // Create realistic surface texture
        difference() {
            // Main body
            translate([-39, -19, 7.5]) cube([78, 38, 2.5]);

            // Random air bubbles and cracks
            for (k = [1:30]) {
                translate([
                    -35 + 70 * (k * 73 % 100) / 100,
                    -15 + 30 * (k * 37 % 100) / 100,
                    9.8
                ]) sphere(d=0.5 + (k % 3) * 0.3, $fn=8);
            }
        }
    }
}

module sugar_crystals() {
    // Small sugar crystals on surface for realistic texture
    color("#E6D3A3") {
        for (i = [1:80]) {
            translate([
                -38 + 76 * (i * 13 % 100) / 100,
                -18 + 36 * (i * 67 % 100) / 100,
                9.5
            ]) rotate([90, 0, i * 45]) 
            scale([0.3, 0.3, 0.8]) 
            cube([1, 1, 0.5]);
        }
    }
}

// Main assembly
union() {
    // Base brittle layer
    brittle_base();

    // Distribute peanuts throughout with realistic clustering
    for (i = [1:18]) {
        peanut_cluster(
            -35 + 70 * (i * 17 % 100) / 100,
            -17 + 34 * (i * 23 % 100) / 100,
            8.2 + (i % 4) * 0.3,
            i * 73
        );
    }

    // Surface sugar crystals
    sugar_crystals();

    // Add broken edge detail on one corner
    color("#CD853F") {
        translate([30, -15, 0]) {
            difference() {
                cube([12, 8, 8]);
                translate([6, 4, 6]) rotate([0, 30, -15]) cube([15, 10, 10]);
            }
        }
    }
}

