// Praline - Realistic style, medium complexity
// A classic chocolate praline with nut filling and textured surface

// Main parameters
praline_size = 40; // Overall diameter in mm
height = 25; // Height of the praline
wall_thickness = 2.5; // Chocolate shell thickness
nut_count = 8; // Number of hazelnut pieces visible on top

// Colors
chocolate_color = "#3E2723"; // Dark chocolate
filling_color = "#D4A574"; // Hazelnut cream
nut_color = "#8B4513"; // Hazelnut

// Main module
module praline() {
    // Chocolate shell - hollowed dome
    difference() {
        // Outer dome
        color(chocolate_color)
        scale([1, 1, 0.8])
        sphere(d = praline_size);

        // Inner cavity
        translate([0, 0, -1]) // Small offset for proper shell
        scale([1, 1, 0.75])
        sphere(d = praline_size - wall_thickness * 2);
    }

    // Flatten the bottom
    difference() {
        union() {
            // Bottom base
            color(chocolate_color)
            translate([0, 0, -height/2])
            cylinder(h = wall_thickness, d = praline_size * 0.95);

            // Top dome with texture
            color(chocolate_color)
            translate([0, 0, wall_thickness/2])
            scale([1, 1, 0.7])
            sphere(d = praline_size);
        }

        // Remove bottom hemisphere
        difference() {
            cube([praline_size * 2, praline_size * 2, height], center = true);
            translate([0, 0, -height])
            sphere(d = praline_size * 1.2);
        }
    }

    // Nut filling visible at bottom
    color(filling_color)
    translate([0, 0, -height/2 + wall_thickness/2])
    cylinder(h = wall_thickness * 1.5, d = praline_size - wall_thickness * 4);

    // Hazelnut pieces on top
    for(i = [0: nut_count-1]) {
        rotate([0, 0, i * (360/nut_count)])
        translate([praline_size * 0.35, 0, height * 0.3])
        rotate([45, 0, 0])
        scale([0.7, 1, 0.5])
        nut_piece();
    }

    // Chocolate texture pattern
    texture_pattern();
}

// Hazelnut piece module
module nut_piece() {
    color(nut_color)
    difference() {
        sphere(d = 6);

        // Nut texture
        for(i = [0: 3]) {
            rotate([0, 0, i * 45])
            translate([0, 0, -1])
            cube([7, 1, 3], center = true);
        }
    }
}

// Surface texture pattern
module texture_pattern() {
    color(chocolate_color)
    for(i = [0: 15]) {
        a = i * (360/15);
        for(j = [0: 5]) {
            r = praline_size/2 - j * 3;
            translate([r * cos(a), r * sin(a), height * 0.6 - j * 0.5])
            sphere(d = 1.2);
        }
    }
}

// Render the praline
praline();

