// Baklava - Traditional Middle-Eastern layered pastry
// Features: phyllo layers, nut filling, diamond cut pattern, honey glaze

// Main dimensions
baklava_width = 60;
baklava_length = 70;
baklava_height = 15;
layer_thickness = 0.8;
nut_layer_thickness = 2;

// Phyllo layer module
module phyllo_layer(width, length, thickness) {
    color("wheat")
    cube([width, length, thickness], center=true);
}

// Nut filling module with texture
module nut_filling(width, length, thickness) {
    color("saddlebrown")
    difference() {
        cube([width, length, thickness], center=true);

        // Create rough texture on top
        for (x = [-width/2+2 : 4 : width/2-2]) {
            for (y = [-length/2+2 : 4 : length/2-2]) {
                translate([x, y, thickness/2-0.1])
                sphere(r=0.5, $fn=8);
            }
        }
    }
}

// Diamond cut pattern on top
module diamond_pattern(width, length, depth) {
    color("goldenrod")
    for (x = [-width/2+5 : 10 : width/2-5]) {
        for (y = [-length/2+5 : 10 : length/2-5]) {
            translate([x, y, depth/2])
            rotate([0, 0, 45])
            linear_extrude(height=depth)
            square([8, 8], center=true);
        }
    }
}

// Honey glaze drips
module honey_glaze() {
    color("darkgoldenrod", 0.7)
    for (i = [0 : 45 : 360]) {
        rotate([0, 0, i])
        translate([baklava_width/3, baklava_length/3, baklava_height/2+0.5])
        cylinder(h=3, r1=1.5, r2=2.5, $fn=8);
    }
}

// Main assembly
union() {
    // Create multiple phyllo layers
    for (i = [-baklava_height/2 : layer_thickness : baklava_height/2-layer_thickness]) {
        translate([0, 0, i])
        phyllo_layer(baklava_width, baklava_length, layer_thickness);
    }

    // Add nut filling layers
    translate([0, 0, -baklava_height/4])
    nut_filling(baklava_width-2, baklava_length-2, nut_layer_thickness);

    translate([0, 0, 0])
    nut_filling(baklava_width-2, baklava_length-2, nut_layer_thickness);

    translate([0, 0, baklava_height/4])
    nut_filling(baklava_width-2, baklava_length-2, nut_layer_thickness);

    // Top phyllo layer with diamond cuts
    translate([0, 0, baklava_height/2-layer_thickness/2])
    phyllo_layer(baklava_width, baklava_length, layer_thickness);

    // Diamond pattern cuts
    translate([0, 0, baklava_height/2-layer_thickness/2])
    diamond_pattern(baklava_width, baklava_length, layer_thickness);

    // Honey glaze
    honey_glaze();

    // Side details - crispy edges
    color("goldenrod")
    for (side = [0, 1]) {
        mirror([0, side, 0])
        translate([0, baklava_length/2+0.5, 0])
        cube([baklava_width, 1, baklava_height], center=false);
    }

    for (side = [0, 1]) {
        mirror([side, 0, 0])
        translate([baklava_width/2+0.5, 0, 0])
        cube([1, baklava_length, baklava_height], center=false);
    }
}

// Profiterole - realistic style with medium complexity
// A choux pastry ball filled with cream and topped with chocolate ganache

// Parameters
prof_size = 45;           // Main pastry diameter
